import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Alert } from 'react-native';
import { httpsCallable } from 'firebase/functions';
import { functions } from '../config/firebase';

const colors = {
  beeYellow: '#F4C430',
  deepNavy: '#1A1B3E',
  white: '#FFFFFF',
};

export default function TelegramChannelsManagerSimple() {
  const [loading, setLoading] = useState(false);

  const testFunction = async () => {
    try {
      console.log('üß™ Testing Firebase connection...');
      const testCallable = httpsCallable(functions, 'testCallable');
      const result = await testCallable();
      console.log('‚úÖ Test result:', result);
      Alert.alert('Success', 'Firebase Functions working!');
    } catch (error) {
      console.error('‚ùå Test failed:', error);
      Alert.alert('Error', String(error));
    }
  };

  const runScraping = async () => {
    setLoading(true);
    try {
      console.log('üöÄ Starting scraping...');
      const runScrapingNow = httpsCallable(functions, 'runTelegramScrapingNow');
      const result = await runScrapingNow();
      console.log('üìä Scraping result:', result);
      Alert.alert('Result', JSON.stringify(result.data, null, 2));
    } catch (error) {
      console.error('üí• Scraping error:', error);
      Alert.alert('Error', String(error));
    } finally {
      setLoading(false);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>üì± Telegram Scraping - Simple Test</Text>

      <TouchableOpacity style={styles.button} onPress={testFunction}>
        <Text style={styles.buttonText}>üß™ Test Firebase Connection</Text>
      </TouchableOpacity>

      <TouchableOpacity 
        style={[styles.button, { backgroundColor: colors.beeYellow }]} 
        onPress={runScraping}
        disabled={loading}
      >
        <Text style={styles.buttonText}>
          {loading ? '‚è≥ Running...' : 'üöÄ Run Scraping'}
        </Text>
      </TouchableOpacity>

      <Text style={styles.info}>
        This is a simplified version to test the core functionality.
        Check the browser console (F12) for detailed logs.
      </Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    backgroundColor: colors.white,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: colors.deepNavy,
    marginBottom: 30,
    textAlign: 'center',
  },
  button: {
    backgroundColor: colors.deepNavy,
    padding: 15,
    borderRadius: 8,
    marginBottom: 15,
    alignItems: 'center',
  },
  buttonText: {
    color: colors.white,
    fontSize: 16,
    fontWeight: 'bold',
  },
  info: {
    marginTop: 20,
    fontSize: 14,
    color: '#666',
    textAlign: 'center',
    fontStyle: 'italic',
  },
});
