// Asset exports for easy importing throughout the app
// Usage: import { BeeIcon, AppIcon } from '../assets';

// App Icons & Logos - Bee-themed NibJobs branding
export const AppIcon = require('../../assets/icon.png');           // Main bee icon
export const NibJobsIcon = require('../../assets/icon.png');       // Use bee icon as main brand
export const BeeIcon = require('../../assets/icon.png');           // Explicit bee icon reference
export const Favicon = require('../../assets/favicon.png');        // Web favicon
export const SplashImage = require('../../assets/splash.png');     // Splash screen

// Backup/Legacy references
export const NibJobsBeeIcon = require('../../assets/nibjobs-icon.png'); // Backup reference
export const AdaptiveIcon = require('../../assets/adaptive-icon.png');  // Android adaptive

// Default export for convenience
export default {
  AppIcon,
  NibJobsIcon,
  BeeIcon,
  Favicon,
  SplashImage,
  NibJobsBeeIcon,
  AdaptiveIcon,
};
