// Jobs Content
interface Job {
  id: string;
  title: string;
  company: string;
  category: string;
  contractType: string;
  location: string;
  salary?: string;
  jobSource: string;
  postedDate?: Date;
  createdAt?: Date;
  isRemote: boolean;
  tags: string[];
}

function JobsContent() {
  const [jobs, setJobs] = useState<Job[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [totalJobs, setTotalJobs] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [lastDoc, setLastDoc] = useState<any>(null);
  const [firstDoc, setFirstDoc] = useState<any>(null);
  const [pageSnapshots, setPageSnapshots] = useState<Map<number, any>>(new Map());
  const colors = lightColors;
  const jobsPerPage = 20;

  // Get total count of jobs
  const getTotalCount = async () => {
    try {
      const jobsRef = collection(db, 'jobs');
      const snapshot = await getDocs(jobsRef);
      setTotalJobs(snapshot.size);
    } catch (err) {
      console.error('Error getting total count:', err);
    }
  };

  const loadJobs = async (page: number = 1, direction: 'next' | 'prev' | 'first' = 'first') => {
    try {
      setLoading(true);
      console.log(`ðŸ“‹ Loading jobs from Firestore - Page ${page}`);

      const jobsRef = collection(db, 'jobs');
      let jobsQuery;

      if (direction === 'first' || page === 1) {
        // First page - no cursor
        jobsQuery = dbQuery(
          jobsRef,
          orderBy('createdAt', 'desc'),
          limit(jobsPerPage)
        );
      } else if (direction === 'next' && lastDoc) {
        // Next page - start after last document
        jobsQuery = dbQuery(
          jobsRef,
          orderBy('createdAt', 'desc'),
          startAfter(lastDoc),
          limit(jobsPerPage)
        );
      } else if (direction === 'prev' && pageSnapshots.has(page)) {
        // Previous page - use cached snapshot
        const cachedDoc = pageSnapshots.get(page);
        jobsQuery = dbQuery(
          jobsRef,
          orderBy('createdAt', 'desc'),
          startAfter(cachedDoc),
          limit(jobsPerPage)
        );
      } else {
        // Fallback to first page
        jobsQuery = dbQuery(
          jobsRef,
          orderBy('createdAt', 'desc'),
          limit(jobsPerPage)
        );
      }

      const querySnapshot = await getDocs(jobsQuery);

      if (querySnapshot.empty) {
        setJobs([]);
        setError('No jobs found');
        setLoading(false);
        return;
      }

      const jobsData: Job[] = [];

      querySnapshot.forEach((doc) => {
        const data = doc.data() as any;
        jobsData.push({
          id: doc.id,
          title: data.title || 'No title',
          company: data.company || 'Unknown company',
          category: data.category || 'Other',
          contractType: data.contractType || 'Full-time',
          location: data.location || 'Not specified',
          salary: data.salary,
          jobSource: data.jobSource || 'Unknown',
          postedDate: data.postedDate ? data.postedDate.toDate() : null,
          createdAt: data.createdAt ? data.createdAt.toDate() : null,
          isRemote: data.isRemote || false,
          tags: data.tags || []
        });
      });

      // Cache first document of current page for previous navigation
      const newPageSnapshots = new Map(pageSnapshots);
      if (querySnapshot.docs.length > 0) {
        newPageSnapshots.set(page, querySnapshot.docs[0]);
        setPageSnapshots(newPageSnapshots);
      }

      // Store last and first documents for pagination
      setLastDoc(querySnapshot.docs[querySnapshot.docs.length - 1]);
      setFirstDoc(querySnapshot.docs[0]);

      console.log(`âœ… Loaded ${jobsData.length} jobs from Firestore (Page ${page})`);
      setJobs(jobsData);
      setCurrentPage(page);
      setError(null);

    } catch (err) {
      console.error('âŒ Error loading jobs:', err);
      setError(err instanceof Error ? err.message : 'Failed to load jobs');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    getTotalCount();
    loadJobs(1, 'first');
  }, []);

  const goToNextPage = () => {
    if (jobs.length === jobsPerPage) {
      loadJobs(currentPage + 1, 'next');
    }
  };

  const goToPrevPage = () => {
    if (currentPage > 1) {
      loadJobs(currentPage - 1, 'prev');
    }
  };

  const totalPages = Math.ceil(totalJobs / jobsPerPage);
  const startIndex = (currentPage - 1) * jobsPerPage + 1;
  const endIndex = Math.min(currentPage * jobsPerPage, totalJobs);

  // Filter jobs based on search (client-side filter on current page only)
  const filteredJobs = jobs.filter(job => 
    searchQuery === '' ||
    job.title?.toLowerCase().includes(searchQuery.toLowerCase()) ||
    job.company?.toLowerCase().includes(searchQuery.toLowerCase()) ||
    job.category?.toLowerCase().includes(searchQuery.toLowerCase()) ||
    job.location?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const formatDate = (date: Date | null | undefined) => {
    if (!date) return 'N/A';
    return date.toLocaleDateString();
  };

  return (
    <View style={styles.contentContainer}>
      {/* Header */}
      <View style={styles.usersHeader}>
        <Text style={styles.contentTitle}>ðŸ’¼ Jobs Management</Text>
        <View style={styles.usersStats}>
          <View style={styles.statBadge}>
            <Text style={styles.statBadgeLabel}>Total Jobs</Text>
            <Text style={styles.statBadgeValue}>{totalJobs}</Text>
          </View>
          <TouchableOpacity 
            style={[styles.statBadge, { backgroundColor: colors.beeYellow }]}
            onPress={() => {
              getTotalCount();
              loadJobs(1, 'first');
            }}
          >
            <Icon name="refresh" size={20} color={colors.deepNavy} />
            <Text style={[styles.statBadgeLabel, { color: colors.deepNavy }]}>Refresh</Text>
          </TouchableOpacity>
        </View>
      </View>

      {/* Search Bar */}
      <View style={styles.searchBarContainer}>
        <Icon name="search" size={20} color={lightColors.warmGray} />
        <TextInput
          style={styles.searchInput}
          placeholder="Search jobs on current page..."
          value={searchQuery}
          onChangeText={setSearchQuery}
        />
        {searchQuery.length > 0 && (
          <TouchableOpacity onPress={() => setSearchQuery('')}>
            <Icon name="close" size={20} color={lightColors.warmGray} />
          </TouchableOpacity>
        )}
      </View>

      {error && (
        <View style={{ padding: 15, backgroundColor: lightColors.danger, borderRadius: 8, marginBottom: 15 }}>
          <Text style={{ color: lightColors.white, fontWeight: 'bold' }}>âŒ Error</Text>
          <Text style={{ color: lightColors.white }}>{error}</Text>
        </View>
      )}

      {loading ? (
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color={lightColors.beeYellow} />
          <Text style={styles.loadingText}>Loading jobs...</Text>
        </View>
      ) : (
        <>
          {/* Jobs Table */}
          <View style={styles.tableContainer}>
            <View style={styles.tableHeader}>
              <Text style={[styles.tableHeaderCell, { flex: 2 }]}>Title</Text>
              <Text style={[styles.tableHeaderCell, { flex: 1.5 }]}>Company</Text>
              <Text style={[styles.tableHeaderCell, { flex: 1 }]}>Category</Text>
              <Text style={[styles.tableHeaderCell, { flex: 1 }]}>Location</Text>
              <Text style={[styles.tableHeaderCell, { flex: 1 }]}>Type</Text>
              <Text style={[styles.tableHeaderCell, { flex: 1 }]}>Source</Text>
              <Text style={[styles.tableHeaderCell, { flex: 1 }]}>Posted</Text>
            </View>

            <ScrollView style={styles.tableBody}>
              {filteredJobs.length === 0 ? (
                <View style={styles.emptyState}>
                  <Icon name="work-outline" size={48} color={lightColors.warmGray} />
                  <Text style={styles.emptyStateText}>
                    {searchQuery ? 'No jobs found matching your search' : 'No jobs available'}
                  </Text>
                </View>
              ) : (
                filteredJobs.map((job, index) => (
                  <View 
                    key={job.id} 
                    style={[
                      styles.tableRow,
                      index % 2 === 0 && styles.tableRowEven
                    ]}
                  >
                    <Text style={[styles.tableCell, { flex: 2 }]} numberOfLines={2}>
                      {job.title}
                    </Text>
                    <Text style={[styles.tableCell, { flex: 1.5 }]} numberOfLines={1}>
                      {job.company}
                    </Text>
                    <Text style={[styles.tableCell, { flex: 1 }]} numberOfLines={1}>
                      {job.category}
                    </Text>
                    <View style={[styles.tableCell, { flex: 1 }]}>
                      <Text numberOfLines={1}>{job.location}</Text>
                      {job.isRemote && (
                        <View style={[styles.statusBadge, { marginTop: 4 }]}>
                          <Text style={styles.statusBadgeText}>Remote</Text>
                        </View>
                      )}
                    </View>
                    <View style={[styles.tableCell, { flex: 1 }]}>
                      <View style={styles.roleBadge}>
                        <Text style={styles.roleBadgeText}>{job.contractType}</Text>
                      </View>
                    </View>
                    <Text style={[styles.tableCell, { flex: 1 }]} numberOfLines={1}>
                      {job.jobSource}
                    </Text>
                    <Text style={[styles.tableCell, { flex: 1 }]} numberOfLines={1}>
                      {formatDate(job.createdAt)}
                    </Text>
                  </View>
                ))
              )}
            </ScrollView>
          </View>

          {/* Pagination */}
          {totalPages > 1 && (
            <View style={styles.paginationContainer}>
              <TouchableOpacity 
                style={[
                  styles.paginationButton,
                  currentPage === 1 && styles.paginationButtonDisabled
                ]}
                onPress={goToPrevPage}
                disabled={currentPage === 1 || loading}
              >
                <Icon name="chevron-left" size={20} color={lightColors.deepNavy} />
              </TouchableOpacity>

              <View style={styles.paginationInfo}>
                <Text style={styles.paginationText}>
                  Page {currentPage} of {totalPages}
                </Text>
                <Text style={styles.paginationSubtext}>
                  Showing {startIndex}-{endIndex} of {totalJobs} jobs
                </Text>
              </View>

              <TouchableOpacity 
                style={[
                  styles.paginationButton,
                  (currentPage === totalPages || jobs.length < jobsPerPage) && styles.paginationButtonDisabled
                ]}
                onPress={goToNextPage}
                disabled={currentPage === totalPages || jobs.length < jobsPerPage || loading}
              >
                <Icon name="chevron-right" size={20} color={lightColors.deepNavy} />
              </TouchableOpacity>
            </View>
          )}
        </>
      )}
    </View>
  );
}

// Users Content
function UsersContent() {
  const [users, setUsers] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchQuery, setSearchQuery] = useState('');
  const [updatingUserId, setUpdatingUserId] = useState<string | null>(null);
  const usersPerPage = 10;

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      console.log('ðŸ” Fetching users from Firestore...');
      const usersRef = collection(db, 'users');

      // Try without ordering first to see if we get any users
      const querySnapshot = await getDocs(usersRef);

      console.log('ðŸ“Š Query snapshot size:', querySnapshot.size);

      const fetchedUsers: any[] = [];
      querySnapshot.forEach((doc) => {
        console.log('ðŸ‘¤ User found:', doc.id, doc.data());
        fetchedUsers.push({
          id: doc.id,
          ...doc.data()
        });
      });

      // Sort by createdAt if it exists, otherwise by email
      fetchedUsers.sort((a, b) => {
        if (a.createdAt && b.createdAt) {
          return b.createdAt.seconds - a.createdAt.seconds;
        }
        return (a.email || '').localeCompare(b.email || '');
      });

      console.log('âœ… Total users fetched:', fetchedUsers.length);
      setUsers(fetchedUsers);
    } catch (error) {
      console.error('âŒ Error fetching users:', error);
      alert('Error fetching users: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleRoleChange = async (userId: string, newRole: string) => {
    try {
      setUpdatingUserId(userId);
      const userRef = doc(db, 'users', userId);
      await updateDoc(userRef, {
        role: newRole,
        updatedAt: new Date()
      });

      // Update local state
      setUsers(users.map(user => 
        user.id === userId ? { ...user, role: newRole } : user
      ));

      alert(`User role updated to ${newRole} successfully!`);
    } catch (error) {
      console.error('Error updating user role:', error);
      alert('Error updating user role: ' + (error as Error).message);
    } finally {
      setUpdatingUserId(null);
    }
  };

  const handleSubscriptionChange = async (userId: string, newSubscriptionId: string) => {
    try {
      setUpdatingUserId(userId);
      const userRef = doc(db, 'users', userId);

      // Determine subscription end date based on plan
      let subscriptionEndDate = null;
      if (newSubscriptionId !== 'free') {
        // Set end date to 1 year from now for paid plans
        subscriptionEndDate = new Date();
        subscriptionEndDate.setFullYear(subscriptionEndDate.getFullYear() + 1);
      }

      await updateDoc(userRef, {
        subscriptionId: newSubscriptionId,
        subscriptionStatus: 'active',
        subscriptionStartDate: new Date(),
        subscriptionEndDate: subscriptionEndDate,
        updatedAt: new Date()
      });

      // Update local state
      setUsers(users.map(user => 
        user.id === userId ? { 
          ...user, 
          subscriptionId: newSubscriptionId,
          subscriptionStatus: 'active',
          subscriptionStartDate: new Date(),
          subscriptionEndDate: subscriptionEndDate
        } : user
      ));

      alert(`User subscription updated to ${newSubscriptionId} successfully!`);
    } catch (error) {
      console.error('Error updating user subscription:', error);
      alert('Error updating user subscription: ' + (error as Error).message);
    } finally {
      setUpdatingUserId(null);
    }
  };

  // Filter users based on search
  const filteredUsers = users.filter(user => 
    user.email?.toLowerCase().includes(searchQuery.toLowerCase()) ||
    user.displayName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
    user.role?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Pagination
  const totalPages = Math.ceil(filteredUsers.length / usersPerPage);
  const startIndex = (currentPage - 1) * usersPerPage;
  const endIndex = startIndex + usersPerPage;
  const currentUsers = filteredUsers.slice(startIndex, endIndex);

  const goToPage = (page: number) => {
    if (page >= 1 && page <= totalPages) {
      setCurrentPage(page);
    }
  };

  const formatDate = (timestamp: any) => {
    if (!timestamp) return 'N/A';
    try {
      return new Date(timestamp.seconds * 1000).toLocaleDateString();
    } catch {
      return 'N/A';
    }
  };

  return (
    <View style={styles.contentContainer}>
      <View style={styles.usersHeader}>
        <Text style={styles.contentTitle}>ðŸ‘¥ User Management</Text>
        <View style={styles.usersStats}>
          <View style={styles.statBadge}>
            <Text style={styles.statBadgeLabel}>Total Users</Text>
            <Text style={styles.statBadgeValue}>{users.length}</Text>
          </View>
          <View style={styles.statBadge}>
            <Text style={styles.statBadgeLabel}>Active</Text>
            <Text style={styles.statBadgeValue}>
              {users.filter(u => u.isActive !== false).length}
            </Text>
          </View>
        </View>
      </View>

      {/* Search Bar */}
      <View style={styles.searchBarContainer}>
        <Icon name="search" size={20} color={lightColors.warmGray} />
        <TextInput
          style={styles.searchInput}
          placeholder="Search by email, name, or role..."
          value={searchQuery}
          onChangeText={setSearchQuery}
        />
        {searchQuery.length > 0 && (
          <TouchableOpacity onPress={() => setSearchQuery('')}>
            <Icon name="close" size={20} color={lightColors.warmGray} />
          </TouchableOpacity>
        )}
      </View>

      {loading ? (
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color={lightColors.beeYellow} />
          <Text style={styles.loadingText}>Loading users...</Text>
        </View>
      ) : (
        <>
          {/* Users Table */}
          <View style={styles.tableContainer}>
            <View style={styles.tableHeader}>
              <Text style={[styles.tableHeaderCell, { flex: 2 }]}>Email</Text>
              <Text style={[styles.tableHeaderCell, { flex: 1.5 }]}>Display Name</Text>
              <Text style={[styles.tableHeaderCell, { flex: 1 }]}>Role</Text>
              <Text style={[styles.tableHeaderCell, { flex: 1 }]}>Subscription</Text>
              <Text style={[styles.tableHeaderCell, { flex: 1 }]}>Status</Text>
              <Text style={[styles.tableHeaderCell, { flex: 1 }]}>Created</Text>
              <Text style={[styles.tableHeaderCell, { flex: 2 }]}>Actions</Text>
            </View>

            <ScrollView style={styles.tableBody}>
              {currentUsers.length === 0 ? (
                <View style={styles.emptyState}>
                  <Icon name="people-outline" size={48} color={lightColors.warmGray} />
                  <Text style={styles.emptyStateText}>
                    {searchQuery ? 'No users found matching your search' : 'No users yet'}
                  </Text>
                </View>
              ) : (
                currentUsers.map((user, index) => (
                  <View 
                    key={user.id} 
                    style={[
                      styles.tableRow,
                      index % 2 === 0 && styles.tableRowEven
                    ]}
                  >
                    <Text style={[styles.tableCell, { flex: 2 }]} numberOfLines={1}>
                      {user.email || 'N/A'}
                    </Text>
                    <Text style={[styles.tableCell, { flex: 1.5 }]} numberOfLines={1}>
                      {user.displayName || user.name || 'N/A'}
                    </Text>
                    <View style={[styles.tableCell, { flex: 1 }]}>
                      <View style={[
                        styles.roleBadge,
                        user.role === 'admin' && styles.roleBadgeAdmin,
                        user.role === 'superadmin' && styles.roleBadgeSuperAdmin,
                        user.role === 'company' && styles.roleBadgeCompany
                      ]}>
                        <Text style={styles.roleBadgeText}>
                          {user.role || 'user'}
                        </Text>
                      </View>
                    </View>
                    <View style={[styles.tableCell, { flex: 1 }]}>
                      <View style={[
                        styles.roleBadge,
                        user.subscriptionId === 'free' && { backgroundColor: '#F3F4F6', borderColor: '#D1D5DB' },
                        user.subscriptionId === 'standard' && { backgroundColor: '#FEF3C7', borderColor: '#F59E0B' },
                        user.subscriptionId === 'enterprise' && { backgroundColor: '#DBEAFE', borderColor: '#3B82F6' }
                      ]}>
                        <Text style={[
                          styles.roleBadgeText,
                          user.subscriptionId === 'free' && { color: '#6B7280' },
                          user.subscriptionId === 'standard' && { color: '#D97706' },
                          user.subscriptionId === 'enterprise' && { color: '#2563EB' }
                        ]}>
                          {user.subscriptionId || 'free'}
                        </Text>
                      </View>
                    </View>
                    <View style={[styles.tableCell, { flex: 1 }]}>
                      <View style={[
                        styles.statusBadge,
                        user.isActive === false && styles.statusBadgeInactive
                      ]}>
                        <Text style={styles.statusBadgeText}>
                          {user.isActive === false ? 'Inactive' : 'Active'}
                        </Text>
                      </View>
                    </View>
                    <Text style={[styles.tableCell, { flex: 1 }]} numberOfLines={1}>
                      {formatDate(user.createdAt)}
                    </Text>
                    <View style={[styles.tableCell, { flex: 2, flexDirection: 'row', alignItems: 'center', gap: 8 }]}>
                      {updatingUserId === user.id ? (
                        <ActivityIndicator size="small" color={lightColors.beeYellow} />
                      ) : (
                        <>
                          <View style={styles.roleSelectContainer}>
                            <select
                              value={user.role || 'user'}
                              onChange={(e) => handleRoleChange(user.id, e.target.value)}
                              style={{
                                padding: '6px 10px',
                                borderRadius: '6px',
                                border: '1px solid #e0e0e0',
                                backgroundColor: '#fff',
                                fontSize: '13px',
                                cursor: 'pointer',
                                outline: 'none',
                                width: '110px'
                              }}
                            >
                              <option value="user">User</option>
                              <option value="company">Company</option>
                              <option value="admin">Admin</option>
                              <option value="superadmin">Super Admin</option>
                            </select>
                          </View>
                          <View style={styles.roleSelectContainer}>
                            <select
                              value={user.subscriptionId || 'free'}
                              onChange={(e) => handleSubscriptionChange(user.id, e.target.value)}
                              style={{
                                padding: '6px 10px',
                                borderRadius: '6px',
                                border: '1px solid #e0e0e0',
                                backgroundColor: '#fff',
                                fontSize: '13px',
                                cursor: 'pointer',
                                outline: 'none',
                                width: '110px'
                              }}
                            >
                              <option value="free">Free</option>
                              <option value="standard">Standard</option>
                              <option value="enterprise">Enterprise</option>
                            </select>
                          </View>
                        </>
                      )}
                    </View>
                  </View>
                ))
              )}
            </ScrollView>
          </View>

          {/* Pagination */}
          {totalPages > 1 && (
            <View style={styles.paginationContainer}>
              <TouchableOpacity 
                style={[
                  styles.paginationButton,
                  currentPage === 1 && styles.paginationButtonDisabled
                ]}
                onPress={() => goToPage(currentPage - 1)}
                disabled={currentPage === 1}
              >
                <Icon name="chevron-left" size={20} color={lightColors.deepNavy} />
              </TouchableOpacity>

              <View style={styles.paginationInfo}>
                <Text style={styles.paginationText}>
                  Page {currentPage} of {totalPages}
                </Text>
                <Text style={styles.paginationSubtext}>
                  Showing {startIndex + 1}-{Math.min(endIndex, filteredUsers.length)} of {filteredUsers.length}
                </Text>
              </View>

              <TouchableOpacity 
                style={[
                  styles.paginationButton,
                  currentPage === totalPages && styles.paginationButtonDisabled
                ]}
                onPress={() => goToPage(currentPage + 1)}
                disabled={currentPage === totalPages}
              >
                <Icon name="chevron-right" size={20} color={lightColors.deepNavy} />
              </TouchableOpacity>
            </View>
          )}
        </>
      )}
    </View>
  );
}

// Subscriptions Content
function SubscriptionsContent() {
  const [editingPlan, setEditingPlan] = useState<string | null>(null);
  const [billingPeriod, setBillingPeriod] = useState<'monthly' | 'yearly'>('monthly');
  const [saving, setSaving] = useState(false);
  const [plans, setPlans] = useState(
    SUBSCRIPTION_PLANS.map(plan => ({
      ...plan,
      priceMonthly: plan.priceMonthly,
      priceYearly: plan.priceYearly,
      features: plan.features.map(f => ({ name: f.text, enabled: f.enabled }))
    }))
  );

  const handlePriceChange = (planId: string, field: 'priceMonthly' | 'priceYearly', value: string) => {
    setPlans(plans.map(plan => 
      plan.id === planId 
        ? { ...plan, [field]: parseFloat(value) || 0 }
        : plan
    ));
  };

  const toggleFeature = (planId: string, featureIndex: number) => {
    setPlans(plans.map(plan => {
      if (plan.id === planId) {
        const newFeatures = [...plan.features];
        newFeatures[featureIndex] = {
          ...newFeatures[featureIndex],
          enabled: !newFeatures[featureIndex].enabled
        };
        return { ...plan, features: newFeatures };
      }
      return plan;
    }));
  };

  const toggleAds = (planId: string) => {
    setPlans(plans.map(plan => 
      plan.id === planId 
        ? { ...plan, adsEnabled: !plan.adsEnabled }
        : plan
    ));
  };

  const savePlanChanges = async (planId: string) => {
    try {
      const plan = plans.find(p => p.id === planId);
      if (!plan) return;

      const planRef = doc(db, 'subscriptionPlans', planId);
      await setDoc(planRef, {
        id: plan.id,
        name: plan.name,
        priceMonthly: plan.priceMonthly,
        priceYearly: plan.priceYearly,
        icon: plan.icon,
        color: plan.color,
        popular: plan.popular,
        buttonText: plan.buttonText,
        features: plan.features,
        adsEnabled: plan.adsEnabled,
        updatedAt: new Date().toISOString()
      });

      alert('Plan updated successfully!');
      setEditingPlan(null);
    } catch (error) {
      console.error('Error updating plan:', error);
      alert('Error updating plan: ' + (error as Error).message);
    }
  };

  const saveAllPlans = async () => {
    try {
      setSaving(true);

      // Save all plans to Firestore
      const savePromises = plans.map(plan => {
        const planRef = doc(db, 'subscriptionPlans', plan.id);
        return setDoc(planRef, {
          id: plan.id,
          name: plan.name,
          priceMonthly: plan.priceMonthly,
          priceYearly: plan.priceYearly,
          icon: plan.icon,
          color: plan.color,
          popular: plan.popular,
          buttonText: plan.buttonText,
          features: plan.features,
          adsEnabled: plan.adsEnabled,
          updatedAt: new Date().toISOString()
        });
      });

      await Promise.all(savePromises);
      alert('All subscription plans saved successfully!');
    } catch (error) {
      console.error('Error saving plans:', error);
      alert('Error saving plans: ' + (error as Error).message);
    } finally {
      setSaving(false);
    }
  };

  return (
    <View style={styles.contentContainer}>
      {/* Header with Save Button */}
      <View style={{ marginBottom: 32 }}>
        <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', marginBottom: 8 }}>
          <View style={{ flex: 1 }}>
            <Text style={{ fontSize: 28, fontFamily: 'Inter_700Bold', color: '#111', marginBottom: 4 }}>
              Subscription Plans
            </Text>
            <Text style={{ fontSize: 15, color: '#6B7280', fontFamily: 'Inter_400Regular' }}>
              Manage pricing, features, and ad settings
            </Text>
          </View>
          <TouchableOpacity
            onPress={saveAllPlans}
            disabled={saving}
            style={{
              backgroundColor: saving ? '#9CA3AF' : '#10B981',
              paddingHorizontal: 20,
              paddingVertical: 12,
              borderRadius: 10,
              flexDirection: 'row',
              alignItems: 'center',
              gap: 8,
              shadowColor: '#10B981',
              shadowOffset: { width: 0, height: 2 },
