    if (typeof window !== 'undefined') {
      window.open(appStoreUrl, '_blank');
    }
  };

  const renderJobCard = ({ item: job }: any) => (
    <View style={dynamicStyles.jobCard}>
      <View style={styles.jobIconContainer}>
        <Icon name="work" size={24} color={colors.deepNavy} />
      </View>
      <View style={styles.jobInfo}>
        <Text style={dynamicStyles.jobTitle} numberOfLines={2}>{job.title || 'Job Title'}</Text>
        <Text style={dynamicStyles.jobCompany} numberOfLines={1}>{job.company || 'Company'}</Text>
        <Text style={dynamicStyles.jobCategory} numberOfLines={1}>{job.category || 'Category'}</Text>
        <Text style={dynamicStyles.jobLocation} numberOfLines={1}>{job.location || 'Location'}</Text>
        {job.salary && <Text style={dynamicStyles.jobSalary} numberOfLines={1}>{job.salary}</Text>}
        <View style={styles.jobBadgesContainer}>
          <View style={[styles.jobBadge, { backgroundColor: colors.lightGray }]}>
            <Text style={dynamicStyles.jobBadgeText}>{job.contractType || 'Full-time'}</Text>
          </View>
        </View>
        <Text style={dynamicStyles.jobSource} numberOfLines={1}>{job.jobSource || 'Source'}</Text>
        <Text style={dynamicStyles.jobDate}>
          {job.createdAt ? new Date(job.createdAt.seconds * 1000).toLocaleDateString() : 'Recently'}
        </Text>
      </View>
    </View>
  );

  const renderChannelCard = ({ item: channel }: any) => (
    <TouchableOpacity style={dynamicStyles.channelCard} activeOpacity={0.8}>
      <View style={styles.channelImageContainer}>
        <View style={styles.channelImageCircle}>
          {channel.imageUrl ? (
            <Image
              source={{ uri: channel.imageUrl }}
              style={styles.channelImage}
              resizeMode="cover"
            />
          ) : (
            <View style={styles.channelDefaultIcon}>
              <Image source={require('./assets/favicon.png')} style={styles.channelFaviconImage} />
            </View>
          )}
        </View>
      </View>
      <Text style={styles.channelName} numberOfLines={1}>{channel.name || channel.username}</Text>
      <Text style={styles.channelMemberCount}>
        {channel.memberCount ? `${channel.memberCount} ${t.members}` : t.telegramChannel}
      </Text>
    </TouchableOpacity>
  );

  return (
    <SafeAreaView style={dynamicStyles.container}>
      {/* Top Toolbar */}
      <View style={dynamicStyles.toolbar}>
        <View style={styles.toolbarLeft}>
          <Image source={require('./assets/favicon.png')} style={styles.appIconImage} />
          <Text style={dynamicStyles.appTitle}>{t.appTitle}</Text>
        </View>

        <View style={styles.toolbarCenter}>
          <View style={dynamicStyles.searchContainer}>
            <Icon name="search" size={20} color={colors.warmGray} />
            <TextInput
              style={dynamicStyles.searchInput}
              placeholder={t.searchPlaceholder}
              value={searchText}
              onChangeText={(text) => {
                setSearchText(text);
                // Real-time search will be handled by useEffect
              }}
              onSubmitEditing={handleSearch}
            />
          </View>
        </View>

        <View style={styles.toolbarRight}>
          {/* Icon-only Theme Switcher */}
          <TouchableOpacity style={styles.iconButton} onPress={toggleTheme}>
            <Icon 
              name={isDarkMode ? "wb-sunny" : "nightlight-round"} 
              size={24} 
              color={colors.deepNavy} 
            />
          </TouchableOpacity>

          {/* Language Dropdown */}
          <View style={styles.languageDropdownContainer}>
            <TouchableOpacity 
              style={styles.languageDropdownButton} 
              onPress={toggleLanguageDropdown}
            >
              <Text style={styles.languageCode}>{getCurrentLanguage().code.toUpperCase()}</Text>
              <Icon 
                name={isLanguageDropdownOpen ? "expand-less" : "expand-more"} 
                size={16} 
                color={colors.deepNavy} 
              />
            </TouchableOpacity>
          </View>

          <TouchableOpacity style={styles.toolbarButton} onPress={handleLogin}>
            <Icon name="login" size={20} color={colors.deepNavy} />
            <Text style={styles.toolbarButtonText}>{t.login}</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.toolbarButton}>
            <Icon name="person-add" size={20} color={colors.deepNavy} />
            <Text style={styles.toolbarButtonText}>{t.signUp}</Text>
          </TouchableOpacity>
        </View>
      </View>

      {/* Full-screen language dropdown overlay - positioned outside toolbar for maximum z-index */}
      {isLanguageDropdownOpen && (
        <View style={styles.fullScreenDropdownOverlay}>
          {/* Invisible touch area to close dropdown */}
          <TouchableOpacity 
            style={styles.fullScreenTouchArea}
            onPress={() => setIsLanguageDropdownOpen(false)}
            activeOpacity={1}
          />

          {/* Language dropdown positioned absolutely */}
          <View style={styles.absoluteLanguageDropdown}>
            {languageOptions.map((language, index) => (
              <TouchableOpacity
                key={language.code}
                style={[
                  styles.languageOption,
                  currentLanguage === language.code && styles.languageOptionActive,
                  index === languageOptions.length - 1 && { borderBottomWidth: 0 }
                ]}
                onPress={() => changeLanguage(language.code)}
              >
                <Text style={styles.languageLabel}>{language.label}</Text>
                {currentLanguage === language.code && (
                  <Icon name="check" size={16} color={colors.beeYellow} />
                )}
              </TouchableOpacity>
            ))}
          </View>
        </View>
      )}

      <View style={styles.mainContent}>
        {/* Left Content - Jobs */}
        <ScrollView style={dynamicStyles.leftContent}>
          {/* Telegram Channels Horizontal Scroll - Centered Wheel */}
          <View style={dynamicStyles.channelsSection}>
            <Text style={dynamicStyles.sectionTitle}>{t.exploreChannels}</Text>
            <View style={styles.channelsContainer}>
              <ScrollView 
                horizontal 
                showsHorizontalScrollIndicator={false} 
                style={styles.channelsScroll}
                contentContainerStyle={styles.channelsScrollContainer}
                decelerationRate="fast"
                snapToInterval={122} // Updated to match new card width + margin (110 + 12)
                snapToAlignment="center"
                pagingEnabled={false}
                bounces={true}
                bouncesZoom={true}
              >
                {telegramChannels.map((channel, index) => (
                  <View key={channel.id || index} style={styles.channelCardWrapper}>
                    {renderChannelCard({ item: channel })}
                  </View>
                ))}
              </ScrollView>
            </View>
            <Text style={styles.channelsHint}>{t.swipeHint}</Text>
          </View>

          {/* Jobs Section */}
          <View style={dynamicStyles.jobsSection}>
            <View style={styles.jobsHeader}>
              <Text style={dynamicStyles.sectionTitle}>{t.recentJobs}</Text>
              <Text style={dynamicStyles.jobsCount}>{jobs.length} {t.jobsFound}</Text>
            </View>

            {loading ? (
              <View style={styles.loadingContainer}>
                <Text style={styles.loadingText}>Loading jobs...</Text>
              </View>
            ) : (
              <FlatList
                data={jobs}
                renderItem={renderJobCard}
                keyExtractor={(item) => item.id}
                numColumns={5}
                showsVerticalScrollIndicator={false}
                contentContainerStyle={styles.jobsGrid}
                scrollEnabled={false}
              />
            )}
          </View>
        </ScrollView>

        {/* Right Sidebar - Filters */}
        <View style={dynamicStyles.filterSidebar}>
          <Text style={dynamicStyles.filterTitle}>Filter Jobs</Text>

          {/* Category Filter */}
          <View style={styles.filterSection}>
            <Text style={dynamicStyles.filterLabel}>Category</Text>
            <ScrollView style={styles.filterScrollView} showsVerticalScrollIndicator={false}>
              <TouchableOpacity 
                style={[dynamicStyles.filterOption, selectedCategory === '' && styles.filterOptionActive]}
                onPress={() => setSelectedCategory('')}
              >
                <Text style={[dynamicStyles.filterOptionText, selectedCategory === '' && styles.filterOptionTextActive]}>
                  All Categories
                </Text>
              </TouchableOpacity>
              {categories.filter(cat => cat.level === 0).map((category) => (
                <TouchableOpacity 
                  key={category.id}
                  style={[dynamicStyles.filterOption, selectedCategory === category.id && styles.filterOptionActive]}
                  onPress={() => setSelectedCategory(category.id)}
                >
                  <Text style={[dynamicStyles.filterOptionText, selectedCategory === category.id && styles.filterOptionTextActive]}>
                    {category.icon} {category.name}
                  </Text>
                </TouchableOpacity>
              ))}
            </ScrollView>
          </View>

          {/* Contract Type Filter */}
          <View style={styles.filterSection}>
            <Text style={dynamicStyles.filterLabel}>Contract Type</Text>
            {['Full-time', 'Part-time', 'Contract', 'Freelance', 'Internship'].map((type) => (
              <TouchableOpacity 
                key={type}
                style={[dynamicStyles.filterOption, selectedContractType === type && styles.filterOptionActive]}
                onPress={() => setSelectedContractType(selectedContractType === type ? '' : type)}
              >
                <Text style={[dynamicStyles.filterOptionText, selectedContractType === type && styles.filterOptionTextActive]}>
                  {type}
                </Text>
              </TouchableOpacity>
            ))}
          </View>

          {/* Remote Work Filter */}
          <View style={styles.filterSection}>
            <Text style={dynamicStyles.filterLabel}>Work Location</Text>
            <TouchableOpacity 
              style={[dynamicStyles.filterOption, isRemote && styles.filterOptionActive]}
              onPress={() => setIsRemote(!isRemote)}
            >
              <Text style={[dynamicStyles.filterOptionText, isRemote && styles.filterOptionTextActive]}>
                ðŸŒ Remote Only
              </Text>
            </TouchableOpacity>
          </View>

          {/* Popular Locations */}
          <View style={styles.filterSection}>
            <Text style={dynamicStyles.filterLabel}>Location</Text>
            {['Addis Ababa', 'Dire Dawa', 'Bahir Dar', 'Hawassa', 'Mekelle', 'Remote'].map((location) => (
              <TouchableOpacity 
                key={location}
                style={[dynamicStyles.filterOption, selectedLocation === location && styles.filterOptionActive]}
                onPress={() => setSelectedLocation(selectedLocation === location ? '' : location)}
              >
                <Text style={[dynamicStyles.filterOptionText, selectedLocation === location && styles.filterOptionTextActive]}>
                  ðŸ“ {location}
                </Text>
              </TouchableOpacity>
            ))}
          </View>

          {/* Clear Filters */}
          <TouchableOpacity style={styles.clearFiltersButton} onPress={() => {
            setSelectedCategory('');
            setSelectedContractType('');
            setSelectedLocation('');
            setIsRemote(false);
            setSearchText('');
          }}>
            <Text style={styles.clearFiltersText}>Clear All Filters</Text>
          </TouchableOpacity>
        </View>
      </View>
    </SafeAreaView>
  );
}

export default function App() {
  const [initializing, setInitializing] = useState(true);
  const [user, setUser] = useState<any>(null);
  const fontsLoaded = useAppFonts();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (authUser) => {
      setUser(authUser);
      if (initializing) setInitializing(false);
    });
    return unsubscribe;
  }, [initializing]);

  if (initializing || !fontsLoaded) {
    return (
      <SafeAreaView style={styles.container}>
        <View style={styles.loadingContainer}>
          <Image source={require('./assets/favicon.png')} style={styles.loadingLogoImage} />
          <Text style={styles.loadingText}>NibJobs</Text>
          <Text style={styles.loadingSubtext}>Loading...</Text>
        </View>
      </SafeAreaView>
    );
  }

  return (
    <NavigationContainer>
      <Stack.Navigator screenOptions={{ headerShown: false }}>
        <Stack.Screen name="Home" component={HomeScreen} />
        <Stack.Screen name="Login" component={LoginScreen} />
        <Stack.Screen name="AdminPanel" component={AdminPanelScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

