// Login Screen
function LoginScreen({ navigation }: any) {
  const [email, setEmail] = useState('nathanmersha@gmail.com');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);

  const handleLogin = async () => {
    if (!email || !password) {
      Alert.alert('Error', 'Please enter email and password');
      return;
    }

    setLoading(true);
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // Reload user to get latest email verification status
      await user.reload();
      const updatedUser = auth.currentUser;

      // Check if email is verified
      if (!updatedUser?.emailVerified) {
        // User hasn't verified their email
        await signOut(auth);
        navigation.navigate('VerificationRequired', { userEmail: email });
        setLoading(false);
        return;
      }

      // Update isVerified in Firestore if email is verified but DB shows false
      const userDoc = await getDoc(doc(db, 'users', user.uid));
      if (userDoc.exists()) {
        const userData = userDoc.data();

        // Update isVerified if it's false but email is verified
        if (!userData.isVerified && updatedUser.emailVerified) {
          await updateDoc(doc(db, 'users', user.uid), {
            isVerified: true,
            updatedAt: new Date()
          });
          console.log('‚úÖ Updated isVerified to true in Firestore');
        }

        // Check if user is admin
        if (userData.role === 'superadmin') {
          navigation.replace('AdminPanel');
        } else {
          Alert.alert('Access Denied', 'You do not have admin privileges');
          await signOut(auth);
        }
      } else {
        Alert.alert('Error', 'User profile not found');
        await signOut(auth);
      }
    } catch (error: any) {
      Alert.alert('Login Failed', error.message);
    }
    setLoading(false);
  };

  const handleGoogleLogin = async () => {
    try {
      const provider = new GoogleAuthProvider();
      const result = await signInWithPopup(auth, provider);
      const user = result.user;

      // Check if user is admin
      const userDoc = await getDoc(doc(db, 'users', user.uid));
      if (userDoc.exists() && userDoc.data().role === 'superadmin') {
        navigation.replace('AdminPanel');
      } else {
        Alert.alert('Access Denied', 'You do not have admin privileges');
        await signOut(auth);
      }
    } catch (error: any) {
      Alert.alert('Google Login Failed', error.message);
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.loginContainer}>
        {/* Header */}
        <View style={styles.loginHeader}>
          <Image source={require('./assets/favicon.png')} style={styles.loginLogoImage} />
          <Text style={styles.loginTitle}>NibJobs</Text>
          <Text style={styles.loginSubtitle}>Administrator Login</Text>
        </View>

        {/* Login Form */}
        <View style={styles.loginForm}>
          <View style={styles.inputContainer}>
            <View style={styles.inputGroup}>
              <Icon name="email" size={20} color={lightColors.warmGray} style={styles.inputIcon} />
              <TextInput
                style={styles.modernInput}
                value={email}
                onChangeText={setEmail}
                placeholder="Email address"
                placeholderTextColor={lightColors.warmGray}
                keyboardType="email-address"
                autoCapitalize="none"
              />
            </View>
          </View>

          <View style={styles.inputContainer}>
            <View style={styles.inputGroup}>
              <Icon name="lock" size={20} color={lightColors.warmGray} style={styles.inputIcon} />
              <TextInput
                style={styles.modernInput}
                value={password}
                onChangeText={setPassword}
                placeholder="Password"
                placeholderTextColor={lightColors.warmGray}
                secureTextEntry
              />
            </View>
          </View>

          <TouchableOpacity 
            style={[styles.modernLoginButton, loading && styles.loginButtonDisabled]} 
            onPress={handleLogin}
            disabled={loading}
          >
            {loading ? (
              <View style={styles.loadingButtonContent}>
                <Text style={styles.modernLoginButtonText}>Signing In...</Text>
              </View>
            ) : (
              <View style={styles.buttonContent}>
                <Text style={styles.modernLoginButtonText}>Sign In</Text>
                <Icon name="arrow-forward" size={20} color={lightColors.white} />
              </View>
            )}
          </TouchableOpacity>

          {/* Divider */}
          <View style={styles.dividerContainer}>
            <View style={styles.divider} />
            <Text style={styles.dividerText}>OR</Text>
            <View style={styles.divider} />
          </View>

          {/* Google Sign In Button */}
          <TouchableOpacity 
            style={styles.googleButton}
            onPress={handleGoogleLogin}
          >
            <Image 
              source={{ uri: 'https://www.gstatic.com/firebasejs/ui/2.0.0/images/auth/google.svg' }}
              style={{ width: 20, height: 20, marginRight: 8 }}
            />
            <Text style={styles.googleButtonText}>Sign in with Google</Text>
          </TouchableOpacity>

          {/* Signup Link */}
          <View style={styles.signupLinkContainer}>
            <Text style={styles.signupLinkText}>Don't have an account? </Text>
            <TouchableOpacity onPress={() => navigation.navigate('Signup')}>
              <Text style={styles.signupLink}>Sign Up</Text>
            </TouchableOpacity>
          </View>
        </View>

        {/* Footer */}
        <TouchableOpacity 
          style={styles.backToHome}
          onPress={() => navigation.navigate('Home')}
        >
          <Icon name="arrow-back" size={16} color={lightColors.softBlue} />
          <Text style={styles.backToHomeText}>Back to Home</Text>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  );
}

// Verification Required Screen
function VerificationRequiredScreen({ route, navigation }: any) {
  const { userEmail } = route.params || {};
  const [loading, setLoading] = useState(false);
  const [resendSuccess, setResendSuccess] = useState(false);

  const handleResendEmail = async () => {
    setLoading(true);
    setResendSuccess(false);

    try {
      // Sign in the user temporarily to resend verification email
      const email = userEmail;
      const password = prompt('Please enter your password to resend verification email:');

      if (!password) {
        Alert.alert('Error', 'Password is required to resend verification email');
        setLoading(false);
        return;
      }

      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // Send verification email with action code settings
      const actionCodeSettings = {
        url: `${window.location.origin}/login`,
        handleCodeInApp: false,
      };

      await sendEmailVerification(user, actionCodeSettings);

      // Sign out immediately
      await signOut(auth);

      setResendSuccess(true);
      Alert.alert(
        'Success',
        'Verification email sent! Please check your inbox and spam folder.',
        [{ text: 'OK' }]
      );
    } catch (error: any) {
      Alert.alert('Error', error.message || 'Failed to resend verification email');
    }

    setLoading(false);
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView contentContainerStyle={styles.loginContainer}>
        <View style={styles.loginHeader}>
          <Image source={require('./assets/favicon.png')} style={styles.loginLogoImage} />
          <Text style={styles.loginTitle}>NibJobs</Text>
          <Text style={styles.loginSubtitle}>Email Verification Required</Text>
        </View>

        <View style={styles.loginForm}>
          <View style={styles.verificationMessageContainer}>
            <Icon name="mark-email-unread" size={64} color={lightColors.beeYellow} style={{ marginBottom: 20 }} />
            <Text style={styles.verificationTitle}>Email Not Verified</Text>
            <Text style={styles.verificationMessage}>
              Your email address has not been verified yet.
            </Text>
            {userEmail && (
              <Text style={styles.verificationEmail}>{userEmail}</Text>
            )}
            <Text style={styles.verificationMessage}>
              Please check your inbox (and spam folder) for the verification email and click the link to verify your account.
            </Text>

            {resendSuccess && (
              <View style={styles.successMessageContainer}>
                <Icon name="check-circle" size={20} color={lightColors.success} />
                <Text style={styles.successMessageText}>Verification email sent!</Text>
              </View>
            )}

            <TouchableOpacity 
              style={[styles.modernLoginButton, loading && styles.loginButtonDisabled]}
              onPress={handleResendEmail}
              disabled={loading}
            >
              {loading ? (
                <View style={styles.loadingButtonContent}>
                  <Text style={styles.modernLoginButtonText}>Sending...</Text>
                </View>
              ) : (
                <View style={styles.buttonContent}>
                  <Icon name="email" size={20} color={lightColors.deepNavy} style={{ marginRight: 8 }} />
                  <Text style={styles.modernLoginButtonText}>Send Verification Email Again</Text>
                </View>
              )}
            </TouchableOpacity>

            <TouchableOpacity 
              style={styles.modernSecondaryButton}
              onPress={() => navigation.navigate('Login')}
            >
              <Text style={styles.modernSecondaryButtonText}>Back to Login</Text>
            </TouchableOpacity>

            <TouchableOpacity 
              style={styles.backToHome}
              onPress={() => navigation.navigate('Home')}
            >
              <Icon name="arrow-back" size={16} color={lightColors.softBlue} />
              <Text style={styles.backToHomeText}>Back to Home</Text>
            </TouchableOpacity>
          </View>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
}

// Signup Screen
function SignupScreen({ navigation }: any) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [fullName, setFullName] = useState('');
  const [loading, setLoading] = useState(false);
  const [emailSent, setEmailSent] = useState(false);
  const [userEmail, setUserEmail] = useState('');

  const handleSignup = async () => {
    if (!email || !password || !fullName) {
      Alert.alert('Error', 'Please fill in all fields');
      return;
    }

    if (password !== confirmPassword) {
      Alert.alert('Error', 'Passwords do not match');
      return;
    }

    if (password.length < 6) {
      Alert.alert('Error', 'Password must be at least 6 characters long');
      return;
    }

    setLoading(true);
    try {
      console.log('üîÑ Creating user account for:', email);
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // Send email verification
      console.log('üìß Sending verification email to:', user.email);
      try {
        const actionCodeSettings = {
          url: `${window.location.origin}/login`, // Redirect to login page after verification
          handleCodeInApp: false,
        };
        await sendEmailVerification(user, actionCodeSettings);
        console.log('‚úÖ Verification email sent successfully!');
      } catch (emailError: any) {
        console.error('‚ùå Failed to send verification email:', emailError);
      }

      // Create user document in Firestore matching superadmin schema
      console.log('üíæ Creating user profile in Firestore...');
      await setDoc(doc(db, 'users', user.uid), {
        uid: user.uid,
        email: email,
        displayName: fullName,
        role: 'user',
        isVerified: false,
        isActive: true,
        lastLogin: new Date(),
        permissions: [],
        profile: {
          avatar: '',
          bio: '',
          phone: ''
        },
        fcmTokens: [],
        selectedCategories: [],
        isNotificationsEnabled: false,
        createdAt: new Date(),
        updatedAt: new Date(),
      });
      console.log('‚úÖ User profile created in Firestore');

      // Set state to show verification message
      setUserEmail(email);
      setEmailSent(true);
    } catch (error: any) {
      console.error('‚ùå Signup failed:', error);
      if (error.code === 'auth/email-already-in-use') {
        Alert.alert('Error', 'This email is already registered');
      } else if (error.code === 'auth/invalid-email') {
        Alert.alert('Error', 'Invalid email address');
      } else {
        Alert.alert('Signup Failed', error.message);
      }
    }
    setLoading(false);
  };

  const handleGoogleSignup = async () => {
    try {
      const provider = new GoogleAuthProvider();
      const result = await signInWithPopup(auth, provider);
      const user = result.user;

      // Check if user document exists, if not create it matching superadmin schema
      const userDoc = await getDoc(doc(db, 'users', user.uid));
      if (!userDoc.exists()) {
        await setDoc(doc(db, 'users', user.uid), {
          uid: user.uid,
          email: user.email || '',
          displayName: user.displayName || 'User',
          role: 'reader',
          isVerified: true, // Google users are pre-verified
          isActive: true,
          lastLogin: new Date(),
          permissions: [],
          profile: {
            avatar: user.photoURL || '',
            bio: '',
            phone: ''
          },
          fcmTokens: [],
          selectedCategories: [],
          isNotificationsEnabled: false,
          createdAt: new Date(),
          updatedAt: new Date(),
        });
      }

      Alert.alert('Success', 'Signed up with Google successfully!');
      navigation.replace('Home');
    } catch (error: any) {
      Alert.alert('Google Signup Failed', error.message);
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView contentContainerStyle={styles.loginContainer}>
        {emailSent ? (
          // Verification Email Sent Message
          <>
            <View style={styles.loginHeader}>
              <Image source={require('./assets/favicon.png')} style={styles.loginLogoImage} />
              <Text style={styles.loginTitle}>NibJobs</Text>
              <Text style={styles.loginSubtitle}>Check Your Email</Text>
            </View>

            <View style={styles.loginForm}>
              <View style={styles.verificationMessageContainer}>
                <Icon name="email" size={64} color={lightColors.beeYellow} style={{ marginBottom: 20 }} />
                <Text style={styles.verificationTitle}>Verification Email Sent!</Text>
                <Text style={styles.verificationMessage}>
                  We've sent a verification email to:
                </Text>
                <Text style={styles.verificationEmail}>{userEmail}</Text>
                <Text style={styles.verificationMessage}>
                  Please check your inbox (and spam folder) and click the verification link to activate your account.
                </Text>

                <TouchableOpacity 
                  style={styles.modernLoginButton}
                  onPress={() => navigation.replace('Login')}
                >
                  <View style={styles.buttonContent}>
                    <Text style={styles.modernLoginButtonText}>Go to Login</Text>
                    <Icon name="arrow-forward" size={20} color={lightColors.deepNavy} />
                  </View>
                </TouchableOpacity>

                <TouchableOpacity 
                  style={styles.backToHome}
                  onPress={() => navigation.navigate('Home')}
                >
                  <Icon name="arrow-back" size={16} color={lightColors.softBlue} />
                  <Text style={styles.backToHomeText}>Back to Home</Text>
                </TouchableOpacity>
              </View>
            </View>
          </>
        ) : (
          // Signup Form
          <>
            <View style={styles.loginHeader}>
              <Image source={require('./assets/favicon.png')} style={styles.loginLogoImage} />
              <Text style={styles.loginTitle}>NibJobs</Text>
              <Text style={styles.loginSubtitle}>Create Your Account</Text>
            </View>

            <View style={styles.loginForm}>
              <View style={styles.inputContainer}>
                <View style={styles.inputGroup}>
                  <Icon name="person" size={20} color={lightColors.warmGray} style={styles.inputIcon} />
                  <TextInput
                    style={styles.modernInput}
                    value={fullName}
                    onChangeText={setFullName}
                    placeholder="Full Name"
                    placeholderTextColor={lightColors.warmGray}
                    autoCapitalize="words"
                  />
                </View>
              </View>

              <View style={styles.inputContainer}>
                <View style={styles.inputGroup}>
                  <Icon name="email" size={20} color={lightColors.warmGray} style={styles.inputIcon} />
                  <TextInput
                    style={styles.modernInput}
                    value={email}
                    onChangeText={setEmail}
                    placeholder="Email address"
                    placeholderTextColor={lightColors.warmGray}
                    keyboardType="email-address"
                    autoCapitalize="none"
                  />
                </View>
              </View>

              <View style={styles.inputContainer}>
                <View style={styles.inputGroup}>
                  <Icon name="lock" size={20} color={lightColors.warmGray} style={styles.inputIcon} />
                  <TextInput
                    style={styles.modernInput}
                    value={password}
                    onChangeText={setPassword}
                    placeholder="Password (min 6 characters)"
                    placeholderTextColor={lightColors.warmGray}
