import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  Modal,
  TouchableOpacity,
  TextInput,
  Switch,
  Image,
  Alert,
  ActivityIndicator
} from 'react-native';
import { collection, addDoc, serverTimestamp } from 'firebase/firestore';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import { db, storage } from '../config/firebase';

interface TelegramChannel {
  id: string;
  username: string;
  name: string;
  imageUrl?: string;
  category: string;
  isActive: boolean;
  scrapingEnabled: boolean;
  totalJobsScraped: number;
  lastScraped: any | null;
  createdAt: any;
  updatedAt: any;
}

const colors = {
  beeYellow: '#F4C430',
  honeyGold: '#D4AF37',
  darkYellow: '#B8860B',
  deepNavy: '#1A1B3E',
  charcoal: '#2C2C2C',
  cream: '#FFF9E6',
  white: '#FFFFFF',
  warmGray: '#6B6B6B',
  lightGray: '#F5F5F5',
  success: '#27AE60',
  danger: '#E74C3C',
  warning: '#F39C12'
};

interface FormData {
  username: string;
  name: string;
  imageUrl: string;
  category: string;
  isActive: boolean;
  scrapingEnabled: boolean;
}

interface TelegramChannelFormModalProps {
  visible: boolean;
  onClose: () => void;
  onChannelAdded: () => void;
}

export default function TelegramChannelFormModal({ 
  visible, 
  onClose, 
  onChannelAdded 
}: TelegramChannelFormModalProps) {
  const [formData, setFormData] = useState<FormData>({
    username: '',
    name: '',
    imageUrl: '',
    category: 'general',
    isActive: true,
    scrapingEnabled: true
  });
  const [selectedImage, setSelectedImage] = useState<string | null>(null);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [isDragging, setIsDragging] = useState(false);

  const resetForm = () => {
    setFormData({
      username: '',
      name: '',
      imageUrl: '',
      category: 'general',
      isActive: true,
      scrapingEnabled: true
    });
    setSelectedImage(null);
    setSelectedFile(null);
    setIsDragging(false);
  };

  const handleClose = () => {
    resetForm();
    onClose();
  };

  const handleImageFile = (file: File) => {
    // Validate file size (max 5MB)
    if (file.size > 5 * 1024 * 1024) {
      Alert.alert('Error', 'Image size should be less than 5MB');
      return false;
    }

    // Validate file type
    if (!file.type.startsWith('image/')) {
      Alert.alert('Error', 'Please select a valid image file');
      return false;
    }

    setSelectedFile(file);
    const imageUrl = URL.createObjectURL(file);
    setSelectedImage(imageUrl);
    return true;
  };

  const pickImage = () => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = 'image/*';
    input.onchange = (e) => {
      const file = (e.target as HTMLInputElement).files?.[0];
      if (file) {
        handleImageFile(file);
      }
    };
    input.click();
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (!isDragging) {
      setIsDragging(true);
    }
  };

  const handleDragEnter = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    // Only set dragging to false if we're leaving the drop zone entirely
    if (!e.currentTarget.contains(e.relatedTarget as Node)) {
      setIsDragging(false);
    }
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);

    const files = Array.from(e.dataTransfer.files);
    if (files.length > 0) {
      const file = files[0];
      handleImageFile(file);
    }
  };

  const uploadImage = async (channelUsername: string, file: File): Promise<string> => {
    try {
      const fileExtension = file.name.split('.').pop() || 'jpg';
      const fileName = `${channelUsername}-${Date.now()}.${fileExtension}`;
      const storageRef = ref(storage, `channel-images/${fileName}`);

      const uploadResult = await uploadBytes(storageRef, file);
      const downloadURL = await getDownloadURL(uploadResult.ref);

      return downloadURL;
    } catch (error) {
      console.error('Error uploading image:', error);
      throw new Error('Failed to upload image');
    }
  };

  const handleSubmit = async () => {
    // Validation
    if (!formData.username.trim() || !formData.name.trim()) {
      Alert.alert('Error', 'Please fill in username and name');
      return;
    }

    // Clean username (remove @ if present)
    const cleanUsername = formData.username.replace('@', '').trim();
    if (cleanUsername.length === 0) {
      Alert.alert('Error', 'Please enter a valid username');
      return;
    }

    setLoading(true);

    try {
      // Check if channel already exists
      // TODO: Add duplicate check here

      // Upload image if selected
      let imageUrl = formData.imageUrl;
      if (selectedFile) {
        setUploading(true);
        try {
          imageUrl = await uploadImage(cleanUsername, selectedFile);
        } catch (error) {
          console.error('Error uploading image:', error);
          Alert.alert('Warning', 'Channel will be added but image upload failed');
        }
        setUploading(false);
      }

      // Create channel document
      const newChannel = {
        username: cleanUsername,
        name: formData.name.trim(),
        imageUrl: imageUrl || null,
        category: formData.category,
        isActive: formData.isActive,
        scrapingEnabled: formData.scrapingEnabled,
        totalJobsScraped: 0,
        lastScraped: null,
        createdAt: serverTimestamp(),
        updatedAt: serverTimestamp()
      };

      // Add to Firestore
      const channelsCollection = collection(db, 'telegramChannels');
      await addDoc(channelsCollection, newChannel);

      Alert.alert('Success', 'Telegram channel added successfully!');
      onChannelAdded();
      handleClose();

    } catch (error) {
      console.error('Error adding channel:', error);
      Alert.alert('Error', 'Failed to add channel. Please try again.');
    }

    setLoading(false);
  };

  return (
    <Modal
      visible={visible}
      animationType="slide"
      transparent={true}
      onRequestClose={handleClose}
    >
      <View style={styles.modalOverlay}>
        <View style={styles.modalContent}>
          <View style={styles.modalHeader}>
            <Text style={styles.modalTitle}>Add Telegram Channel</Text>
            <TouchableOpacity onPress={handleClose} style={styles.closeButton}>
              <Text style={styles.closeButtonText}>âœ•</Text>
            </TouchableOpacity>
          </View>

          <View style={styles.form}>
            {/* Username Input */}
            <View style={styles.inputGroup}>
              <Text style={styles.label}>Username *</Text>
              <TextInput
                style={styles.input}
                value={formData.username}
                onChangeText={(text) => setFormData({ ...formData, username: text })}
                placeholder="e.g., jobtelegram"
                placeholderTextColor={colors.warmGray}
                autoCapitalize="none"
              />
              <Text style={styles.helpText}>Enter without @ symbol</Text>
            </View>

            {/* Name Input */}
            <View style={styles.inputGroup}>
              <Text style={styles.label}>Channel Name *</Text>
              <TextInput
                style={styles.input}
                value={formData.name}
                onChangeText={(text) => setFormData({ ...formData, name: text })}
                placeholder="e.g., Job Telegram Channel"
                placeholderTextColor={colors.warmGray}
              />
            </View>

            {/* Category Input */}
            <View style={styles.inputGroup}>
              <Text style={styles.label}>Category</Text>
              <TextInput
                style={styles.input}
                value={formData.category}
                onChangeText={(text) => setFormData({ ...formData, category: text })}
                placeholder="e.g., technology, marketing, general"
                placeholderTextColor={colors.warmGray}
              />
            </View>

            {/* Image Upload */}
            <View style={styles.inputGroup}>
              <Text style={styles.label}>Channel Image (Optional)</Text>
              <View 
                style={[
                  styles.imagePickerButton,
                  isDragging && styles.imagePickerButtonDragging
                ]} 
                onDragOver={handleDragOver}
                onDragEnter={handleDragEnter}
                onDragLeave={handleDragLeave}
                onDrop={handleDrop}
              >
                <TouchableOpacity 
                  style={styles.imagePickerTouchable}
                  onPress={pickImage}
                  activeOpacity={0.8}
                >
                  {selectedImage ? (
                    <View style={styles.selectedImageContainer}>
                      <Image source={{ uri: selectedImage }} style={styles.selectedImage} />
                      <Text style={styles.changeImageText}>
                        {isDragging ? 'Drop image here!' : 'Tap to change or drag & drop new image'}
                      </Text>
                    </View>
                  ) : (
                    <View style={styles.imagePickerContent}>
                      <Text style={styles.imagePickerIcon}>
                        {isDragging ? 'ðŸ“¤' : 'ðŸ“·'}
                      </Text>
                      <Text style={[
                        styles.imagePickerText,
                        isDragging && styles.imagePickerTextDragging
                      ]}>
                        {isDragging ? 'Drop image here!' : 'Tap to select or drag & drop image'}
                      </Text>
                      <Text style={styles.imagePickerHint}>
                        Supports: JPG, PNG, GIF â€¢ Max size: 5MB
                      </Text>
                    </View>
                  )}
                </TouchableOpacity>
              </View>
              {uploading && (
                <View style={styles.uploadingContainer}>
                  <ActivityIndicator size="small" color={colors.beeYellow} />
                  <Text style={styles.uploadingText}>Uploading image...</Text>
                </View>
              )}
            </View>

            {/* Active Status */}
            <View style={styles.switchGroup}>
              <Text style={styles.label}>Active Status</Text>
              <View style={styles.switchRow}>
                <Text style={styles.switchLabel}>Channel Active</Text>
                <Switch
                  value={formData.isActive}
                  onValueChange={(value) => setFormData({ ...formData, isActive: value })}
                  trackColor={{ false: colors.lightGray, true: colors.success }}
                  thumbColor={formData.isActive ? colors.white : colors.warmGray}
                />
              </View>
              <View style={styles.switchRow}>
                <Text style={styles.switchLabel}>Scraping Enabled</Text>
                <Switch
                  value={formData.scrapingEnabled}
                  onValueChange={(value) => setFormData({ ...formData, scrapingEnabled: value })}
                  trackColor={{ false: colors.lightGray, true: colors.beeYellow }}
                  thumbColor={formData.scrapingEnabled ? colors.white : colors.warmGray}
                />
              </View>
            </View>
          </View>

          {/* Action Buttons */}
          <View style={styles.actionButtons}>
            <TouchableOpacity style={styles.cancelButton} onPress={handleClose}>
              <Text style={styles.cancelButtonText}>Cancel</Text>
            </TouchableOpacity>
            <TouchableOpacity 
              style={[styles.submitButton, loading && styles.submitButtonDisabled]} 
              onPress={handleSubmit}
              disabled={loading}
            >
              {loading ? (
                <ActivityIndicator size="small" color={colors.white} />
              ) : (
                <Text style={styles.submitButtonText}>Add Channel</Text>
              )}
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </Modal>
  );
}

