import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  ScrollView,
  Alert,
} from 'react-native';
import { collection, getDocs } from 'firebase/firestore';
import { httpsCallable } from 'firebase/functions';
import { db, functions } from '../config/firebase';

const colors = {
  beeYellow: '#F4C430',
  honeyGold: '#D4AF37',
  darkYellow: '#B8860B',
  deepNavy: '#1A1B3E',
  charcoal: '#2C2C2C',
  cream: '#FFF9E6',
  white: '#FFFFFF',
  warmGray: '#6B6B6B',
  lightGray: '#F5F5F5',
  success: '#27AE60',
  danger: '#E74C3C',
  warning: '#F39C12'
};

interface TelegramChannel {
  id: string;
  username: string;
  name: string;
  imageUrl?: string;
  category: string;
  isActive: boolean;
  scrapingEnabled: boolean;
  totalJobsScraped: number;
  lastScraped?: Date;
}

export default function TelegramChannelsManager() {
  const [channels, setChannels] = useState<TelegramChannel[]>([]);
  const [loading, setLoading] = useState(false);
  const [lastUpdate, setLastUpdate] = useState<Date>(new Date());
  const [scrapingLogs, setScrapingLogs] = useState<string[]>([]);
  const [scrapingStatus, setScrapingStatus] = useState<{
    isRunning: boolean;
    currentStep: string;
    progress: number;
    startTime?: Date;
    lastActivity?: Date;
  }>({
    isRunning: false,
    currentStep: '',
    progress: 0
  });

  // Load channels from Firestore
  const loadChannels = async () => {
    try {
      setLoading(true);
      const channelsSnapshot = await getDocs(collection(db, 'telegramChannels'));
      const channelsData = channelsSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      })) as TelegramChannel[];

      setChannels(channelsData);
      setLastUpdate(new Date());
    } catch (error) {
      console.error('Error loading channels:', error);
      Alert.alert('Error', 'Failed to load telegram channels');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadChannels();
  }, []);

  // Helper function to add logs
  const addLog = (message: string) => {
    const timestamp = new Date().toLocaleTimeString();
    const logMessage = `[${timestamp}] ${message}`;
    setScrapingLogs(prev => [...prev.slice(-20), logMessage]); // Keep last 20 logs
    console.log(logMessage);
  };

  // Helper function to update scraping status
  const updateStatus = (step: string, progress: number = 0) => {
    setScrapingStatus(prev => ({
      ...prev,
      currentStep: step,
      progress,
      lastActivity: new Date()
    }));
    addLog(step);
  };

  const handleManualTrigger = async () => {
    Alert.alert(
      'Run Scraping Now üöÄ',
      `This will immediately run the Telegram scraping function using your authentication credentials.\n\nIt will:\n‚Ä¢ Connect to Telegram with your account\n‚Ä¢ Process all ${channels.filter(c => c.isActive && c.scrapingEnabled).length} active channels\n‚Ä¢ Extract jobs using OpenAI\n‚Ä¢ Save results to Firestore\n\nThis may take 2-5 minutes depending on the number of channels.`,
      [
        { text: 'Cancel', style: 'cancel' },
        { 
          text: 'Run Now', 
          onPress: async () => {
            // Initialize scraping state
            setLoading(true);
            setScrapingLogs([]);
            setScrapingStatus({
              isRunning: true,
              currentStep: 'Initializing...',
              progress: 0,
              startTime: new Date()
            });

            try {
              addLog('üöÄ Starting manual Telegram scraping...');
              updateStatus('üìã Connecting to Firebase Functions...', 10);

              const runScrapingNow = httpsCallable(functions, 'runTelegramScrapingNow');

              updateStatus('üîê Authenticating with Telegram...', 20);
              addLog('üì° Sending request to cloud function...');

              const result = await runScrapingNow();

              updateStatus('‚úÖ Function executed successfully', 90);
              addLog('üìä Processing response...');

              console.log('üìä Manual scraping result:', result);
              const data = result.data as any;

              if (data.success) {
                updateStatus(`üéâ Complete! Extracted ${data.totalJobs} jobs`, 100);
                addLog(`‚úÖ Processed ${data.totalChannels} channels`);
                addLog(`üì• Analyzed ${data.totalMessages} messages`);
                addLog(`üéØ Extracted ${data.totalJobs} jobs`);
                addLog('üíæ Jobs saved to Firestore database');

                Alert.alert(
                  'Scraping Complete! üéâ',
                  `‚úÖ Successfully processed ${data.totalChannels} channels\nüì• Analyzed ${data.totalMessages} messages\nüéØ Extracted ${data.totalJobs} jobs\n\nüíæ Check Firestore jobs collection for results!`,
                  [
                    { 
                      text: 'View Details', 
                      onPress: () => {
                        console.log('üìã Detailed scraping results:');
                        console.log(`Total Channels: ${data.totalChannels}`);
                        console.log(`Total Messages: ${data.totalMessages}`);
                        console.log(`Total Jobs: ${data.totalJobs}`);
                        console.log('Channel Results:');
                        data.channelResults.forEach((channel: any, index: number) => {
                          if (channel.error) {
                            console.log(`${index + 1}. @${channel.username}: ERROR - ${channel.error}`);
                          } else {
                            console.log(`${index + 1}. @${channel.username}: ${channel.jobsExtracted} jobs from ${channel.messagesProcessed} messages`);
                          }
                        });
                        console.log('\nüîó View jobs: https://console.firebase.google.com/project/nibjobs-dev/firestore/data/~2Fjobs');
                      }
                    },
                    { text: 'OK' }
                  ]
                );

                // Refresh channels to see updated stats
                setTimeout(() => {
                  loadChannels();
                }, 2000);

              } else {
                updateStatus('‚ùå Scraping failed', 0);
                addLog(`‚ùå Error: ${data.error}`);
                addLog('üîç Check Firebase logs for details');

                Alert.alert(
                  'Scraping Failed ‚ùå',
                  `Error: ${data.error}\n\nCheck Firebase Functions logs for more details.`,
                  [
                    { 
                      text: 'View Logs', 
                      onPress: () => {
                        console.log('ÔøΩ View Firebase logs: https://console.firebase.google.com/project/nibjobs-dev/functions/logs');
                        console.log('üîç Search for "runTelegramScrapingNow" in the logs');
                      }
                    },
                    { text: 'OK' }
                  ]
                );
              }

            } catch (error) {
              updateStatus('üí• Function error occurred', 0);
              addLog(`üí• Error: ${error}`);
              addLog('üîç Check browser console for details');

              console.error('üí• Error running manual scraping:', error);
              Alert.alert(
                'Function Error',
                `Failed to execute scraping function:\n\n${error}\n\nThis might be due to:\n‚Ä¢ Missing environment variables\n‚Ä¢ Telegram authentication issues\n‚Ä¢ Network problems`,
                [
                  { 
                    text: 'View Console', 
                    onPress: () => {
                      console.log('üîç Check browser console (F12) for detailed error information');
                      console.log('üìä Check Firebase logs: https://console.firebase.google.com/project/nibjobs-dev/functions/logs');
                    }
                  },
                  { text: 'OK' }
                ]
              );
            } finally {
              setLoading(false);
              setScrapingStatus(prev => ({ ...prev, isRunning: false }));
              addLog('üèÅ Scraping session ended');
            }
          } 
        }
      ]
    );
  };  const handleScheduledScraping = async () => {
    Alert.alert(
      'Scheduled Telegram Scraping',
      'This function runs automatically at 9 AM UTC daily. It:\n\nüîê Uses your Telegram user credentials\nüìã Queries all active channels\nüì• Fetches latest 10 messages per channel\nü§ñ Uses OpenAI for job extraction\nüíæ Saves jobs to Firestore\nüö´ Avoids duplicates',
      [
        { text: 'View Implementation', onPress: () => {
          console.log('üîç Function Implementation Details:');
          console.log('1. üîê Telegram Authentication: Uses TELEGRAM_SESSION_STRING, API_ID, API_HASH');
          console.log('2. üìã Channel Query: WHERE isActive==true AND scrapingEnabled==true');
          console.log('3. üì• Message Fetching: Latest 10 messages per channel via MTProto API');
          console.log('4. ü§ñ AI Processing: OpenAI GPT-3.5-turbo for job extraction');
          console.log('5. üíæ Data Storage: Firestore jobs collection with deduplication');
          console.log('6. üìä Statistics: Updates channel stats (totalJobsScraped, lastScraped)');
        }},
        { text: 'OK' }
      ]
    );
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>üì± Telegram Scraping System</Text>
        <Text style={styles.subtitle}>
          Automated job scraping using Telegram User API + OpenAI
        </Text>
      </View>

      {/* System Status */}
      <View style={styles.statusContainer}>
        <Text style={styles.statusTitle}>ü§ñ System Status</Text>
        <Text style={styles.statusText}>
          ‚úÖ Scheduled Function: Active (9 AM UTC daily)
        </Text>
        <Text style={styles.statusText}>
          üîê Authentication: Telegram User API 
        </Text>
        <Text style={styles.statusText}>
          üìä Active Channels: {channels.filter(c => c.isActive && c.scrapingEnabled).length}
        </Text>
        <Text style={styles.statusText}>
          üîÑ Last Updated: {lastUpdate.toLocaleString()}
        </Text>
      </View>

      {/* Channel Statistics */}
      <View style={styles.channelsContainer}>
        <Text style={styles.channelsTitle}>üìã Active Channels</Text>

        {loading ? (
          <Text style={styles.loadingText}>Loading channels...</Text>
        ) : channels.length === 0 ? (
          <Text style={styles.noChannelsText}>No channels configured</Text>
        ) : (
          channels
            .filter(c => c.isActive && c.scrapingEnabled)
            .map(channel => (
              <View key={channel.id} style={styles.channelItem}>
                <View style={styles.channelHeader}>
                  <Text style={styles.channelName}>@{channel.username}</Text>
                  <View style={styles.statusBadge}>
                    <Text style={styles.statusBadgeText}>Active</Text>
                  </View>
                </View>
                <Text style={styles.channelDetails}>
                  {channel.name} ‚Ä¢ {channel.category}
                </Text>
                <Text style={styles.channelStats}>
                  üìä Jobs Scraped: {channel.totalJobsScraped || 0}
                </Text>
                {channel.lastScraped && (
                  <Text style={styles.channelStats}>
                    üïê Last Scraped: {new Date(channel.lastScraped).toLocaleString()}
                  </Text>
                )}
              </View>
            ))
        )}

        {channels.filter(c => !c.isActive || !c.scrapingEnabled).length > 0 && (
          <>
            <Text style={styles.inactiveTitle}>üí§ Inactive Channels</Text>
            {channels
              .filter(c => !c.isActive || !c.scrapingEnabled)
              .map(channel => (
                <View key={channel.id} style={[styles.channelItem, styles.inactiveChannel]}>
                  <Text style={styles.inactiveChannelName}>@{channel.username}</Text>
                  <Text style={styles.channelDetails}>{channel.name}</Text>
                </View>
              ))
            }
          </>
        )}
      </View>

      {/* Real-time Scraping Status & Logs */}
      {(scrapingStatus.isRunning || scrapingLogs.length > 0) && (
        <View style={styles.loggingContainer}>
          <Text style={styles.loggingTitle}>üîç Scraping Activity</Text>

          {/* Current Status */}
          {scrapingStatus.isRunning && (
            <View style={styles.statusPanel}>
              <Text style={styles.currentStatus}>
                Status: {scrapingStatus.currentStep}
              </Text>
              <View style={styles.progressBar}>
                <View 
                  style={[
                    styles.progressFill, 
                    { width: `${scrapingStatus.progress}%` }
                  ]} 
                />
              </View>
              <Text style={styles.progressText}>
                {scrapingStatus.progress}% Complete
              </Text>
              {scrapingStatus.startTime && (
                <Text style={styles.timeText}>
                  ‚è±Ô∏è Started: {scrapingStatus.startTime.toLocaleTimeString()}
                </Text>
              )}
            </View>
          )}

          {/* Live Logs */}
          {scrapingLogs.length > 0 && (
            <View style={styles.logsPanel}>
              <Text style={styles.logsTitle}>üìã Activity Logs</Text>
              <ScrollView 
                style={styles.logsScroll}
                showsVerticalScrollIndicator={true}
              >
                {scrapingLogs.map((log, index) => (
                  <Text key={index} style={styles.logEntry}>
                    {log}
                  </Text>
                ))}
              </ScrollView>
              <TouchableOpacity 
                style={styles.clearLogsButton}
                onPress={() => setScrapingLogs([])}
              >
                <Text style={styles.clearLogsText}>üóëÔ∏è Clear Logs</Text>
              </TouchableOpacity>
            </View>
          )}
        </View>
      )}

      {/* Action Buttons */}
      <View style={styles.actionContainer}>
        <TouchableOpacity 
          style={styles.actionButton}
          onPress={handleScheduledScraping}
          disabled={loading}
        >
          <Text style={styles.actionButtonText}>
            ‚ÑπÔ∏è About Scheduled Function
          </Text>
        </TouchableOpacity>

        <TouchableOpacity 
          style={styles.manualButton}
          onPress={handleManualTrigger}
          disabled={loading}
        >
          <Text style={styles.manualButtonText}>
            {loading ? 'ÔøΩ Scraping in Progress...' : 'üöÄ Run Scraping NOW'}
          </Text>
        </TouchableOpacity>

        <TouchableOpacity 
          style={styles.refreshButton}
          onPress={loadChannels}
          disabled={loading}
        >
          <Text style={styles.refreshButtonText}>
            {loading ? 'üîÑ Loading...' : 'üîÑ Refresh Channels'}
          </Text>
        </TouchableOpacity>
      </View>

      {/* Information */}
      <View style={styles.infoContainer}>
        <Text style={styles.infoTitle}>üîß How It Works</Text>
        <Text style={styles.infoText}>
          1. üïò Runs daily at 9 AM UTC automatically
        </Text>
        <Text style={styles.infoText}>
          2. üîê Connects with your Telegram user account
        </Text>
        <Text style={styles.infoText}>
          3. üìã Queries all active channels from Firestore
        </Text>
        <Text style={styles.infoText}>
          4. üì• Fetches latest 10 messages from each channel
        </Text>
        <Text style={styles.infoText}>
          5. ü§ñ Uses OpenAI to extract job information
        </Text>
        <Text style={styles.infoText}>
          6. üíæ Saves validated jobs to Firestore
        </Text>
        <Text style={styles.infoText}>
          7. üö´ Automatically prevents duplicate jobs
        </Text>
      </View>

      {/* Requirements */}
      <View style={styles.requirementsContainer}>
        <Text style={styles.infoTitle}>‚öôÔ∏è Environment Variables Required</Text>
        <Text style={styles.requirementText}>
          ‚Ä¢ TELEGRAM_API_ID: Your Telegram app ID
        </Text>
        <Text style={styles.requirementText}>
          ‚Ä¢ TELEGRAM_API_HASH: Your Telegram app hash  
        </Text>
        <Text style={styles.requirementText}>
          ‚Ä¢ TELEGRAM_SESSION_STRING: User session for auth
        </Text>
        <Text style={styles.requirementText}>
          ‚Ä¢ OPENAI_API_KEY: OpenAI API for job extraction
        </Text>
      </View>
    </ScrollView>
  );
}

