import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

// Web styles
const webStyles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f5f5f5',
    padding: 20,
  },
  title: {
    fontSize: 32,
    fontWeight: 'bold',
    color: '#007AFF',
    marginBottom: 10,
  },
  subtitle: {
    fontSize: 18,
    color: '#666',
    marginBottom: 20,
  },
  description: {
    fontSize: 16,
    textAlign: 'center',
    marginBottom: 20,
    color: '#333',
    maxWidth: 600,
  },
  note: {
    fontSize: 14,
    fontStyle: 'italic',
    color: '#888',
    marginBottom: 30,
  },
  features: {
    alignItems: 'flex-start',
    backgroundColor: '#fff',
    padding: 20,
    borderRadius: 10,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3,
  },
  featureTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 15,
    color: '#333',
  },
  feature: {
    fontSize: 16,
    marginBottom: 8,
    color: '#666',
    lineHeight: 24,
  },
  backendStatus: {
    backgroundColor: '#e8f5e8',
    padding: 15,
    borderRadius: 8,
    marginTop: 20,
    alignItems: 'center',
  },
  statusTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#2d5a2d',
    marginBottom: 5,
  },
  statusText: {
    fontSize: 14,
    color: '#2d5a2d',
    textAlign: 'center',
  },
});

// Simple web app for demonstration
export default function App() {
  return (
    <View style={webStyles.container}>
      <Text style={webStyles.title}>üéØ NibJobs</Text>
      <Text style={webStyles.subtitle}>AI-Powered Job Aggregation Platform</Text>
      <Text style={webStyles.description}>
        Welcome to NibJobs! This platform automatically aggregates job opportunities from Telegram channels using advanced AI extraction technology.
      </Text>
      <Text style={webStyles.note}>
        üì± Full functionality is available on our mobile app
      </Text>
      <View style={webStyles.features}>
        <Text style={webStyles.featureTitle}>üöÄ Platform Features:</Text>
        <Text style={webStyles.feature}>ü§ñ AI-powered job extraction from Telegram channels</Text>
        <Text style={webStyles.feature}>üìä Smart categorization and filtering</Text>
        <Text style={webStyles.feature}>üîî Real-time push notifications</Text>
        <Text style={webStyles.feature}>üë§ Personalized user profiles</Text>
        <Text style={webStyles.feature}>üéØ Category-based job matching</Text>
        <Text style={webStyles.feature}>‚òÅÔ∏è Firebase backend infrastructure</Text>
      </View>

      <View style={webStyles.backendStatus}>
        <Text style={webStyles.statusTitle}>‚úÖ Backend Status</Text>
        <Text style={webStyles.statusText}>
          Firebase Functions deployed and operational{'\n'}
          OpenAI integration active{'\n'}
          Daily job scraping scheduled
        </Text>
      </View>
    </View>
  );
}
