    fetchJobs();
    fetchTelegramChannels();
    fetchCategories();
  }, []);

  useEffect(() => {
    filterJobs();
  }, [allJobs, searchText, selectedCategory, selectedContractType, selectedLocation, isRemote]);

  const filterJobs = () => {
    let filtered = [...allJobs];

    // Search filter
    if (searchText.trim()) {
      const searchLower = searchText.toLowerCase();
      filtered = filtered.filter(job => 
        (job.title || '').toLowerCase().includes(searchLower) ||
        (job.company || '').toLowerCase().includes(searchLower) ||
        (job.description || '').toLowerCase().includes(searchLower) ||
        (job.category || '').toLowerCase().includes(searchLower) ||
        (job.location || '').toLowerCase().includes(searchLower)
      );
    }

    // Category filter
    if (selectedCategory) {
      filtered = filtered.filter(job => 
        job.categoryId === selectedCategory || 
        job.mainCategoryId === selectedCategory ||
        job.category === selectedCategory
      );
    }

    // Contract type filter
    if (selectedContractType) {
      filtered = filtered.filter(job => job.contractType === selectedContractType);
    }

    // Location filter
    if (selectedLocation) {
      if (selectedLocation === 'Remote') {
        filtered = filtered.filter(job => job.isRemote === true);
      } else {
        filtered = filtered.filter(job => 
          (job.location || '').toLowerCase().includes(selectedLocation.toLowerCase())
        );
      }
    }

    // Remote filter
    if (isRemote) {
      filtered = filtered.filter(job => job.isRemote === true);
    }

    setFilteredJobs(filtered);
    setJobs(filtered);
  };

  const getJobTypeColor = (type: string | undefined) => {
    if (!type) return colors.deepNavy;

    switch (type.toLowerCase()) {
      case 'full-time': return colors.success;
      case 'part-time': return colors.softBlue;
      case 'contract': return colors.beeYellow;
      case 'freelance': return colors.darkYellow;
      case 'internship': return colors.warmGray;
      default: return colors.deepNavy;
    }
  };

  const fetchJobs = async () => {
    try {
      const jobsQuery = query(
        collection(db, 'jobs'),
        orderBy('createdAt', 'desc'),
        limit(50)
      );
      const jobsSnapshot = await getDocs(jobsQuery);
      const fetchedJobs = jobsSnapshot.docs.map(doc => {
        const data = doc.data();
        return {
          id: doc.id,
          title: data.title || 'Untitled Job',
          company: data.company || 'Unknown Company',
          location: data.location || 'Remote',
          type: data.type || 'full-time',
          category: data.category || 'general',
          salary: data.salary || '',
          description: data.description || '',
          requirements: data.requirements || [],
          remote: data.remote || false,
          urgent: data.urgent || false,
          views: data.views || 0,
          applications: data.applications || 0,
          createdAt: data.createdAt || null,
          ...data
        };
      });
      setAllJobs(fetchedJobs);
      setJobs(fetchedJobs);
      setFilteredJobs(fetchedJobs);
    } catch (error) {
      console.error('Error fetching jobs:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchTelegramChannels = async () => {
    try {
      const channelsQuery = query(
        collection(db, 'telegramChannels')
        // Removed limit to show all telegram channels
      );
      const channelsSnapshot = await getDocs(channelsQuery);
      const fetchedChannels = channelsSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setTelegramChannels(fetchedChannels);
    } catch (error) {
      console.error('Error fetching telegram channels:', error);
    }
  };

  const fetchCategories = async () => {
    try {
      const categoriesQuery = collection(db, 'categories');
      const categoriesSnapshot = await getDocs(categoriesQuery);
      const fetchedCategories = categoriesSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      // Sort manually in the client
      fetchedCategories.sort((a: any, b: any) => {
        if (a.level !== b.level) {
          return (a.level || 0) - (b.level || 0);
        }
        return (a.order || 0) - (b.order || 0);
      });
      setCategories(fetchedCategories);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

