              </TouchableOpacity>
              <Text style={[styles.jobFooterLinkDivider, { color: colors.warmGray }]}>â€¢</Text>
              <TouchableOpacity>
                <Text style={[styles.footerLinkText, { color: colors.warmGray }]}>Contact Us</Text>
              </TouchableOpacity>
            </View>
          </View>
        </ScrollView>

        {/* Sticky Apply Button - Always Visible */}
