  const colors = isDarkMode ? darkColors : lightColors;

  const saveProfile = async () => {
    if (!user) return;

    try {
      setSaving(true);
      await updateDoc(doc(db, 'users', user.uid), {
        displayName,
        dateOfBirth,
        profilePicture,
      });
      Alert.alert('Success', 'Profile updated successfully!');
      navigation.goBack();
