import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  SafeAreaView,
  RefreshControl,
  Alert,
  ActivityIndicator,
  Image,
} from 'react-native';
import { collection, getDocs, query, orderBy, writeBatch, doc, serverTimestamp, onSnapshot } from 'firebase/firestore';
import { httpsCallable } from 'firebase/functions';
import { db, functions } from '../config/firebase';
import TelegramChannelFormModal from '../components/TelegramChannelFormModal';
import TelegramChannelsList from '../components/TelegramChannelsList';
import { NibJobsIcon } from '../assets';
import Icon from 'react-native-vector-icons/MaterialIcons';

const defaultColors = {
  beeYellow: '#F4C430',
  honeyGold: '#D4AF37',
  darkYellow: '#B8860B',
  deepNavy: '#1A1B3E',
  charcoal: '#2C2C2C',
  cream: '#FFF9E6',
  white: '#FFFFFF',
  warmGray: '#6B6B6B',
  lightGray: '#F5F5F5',
  success: '#27AE60',
  danger: '#E74C3C',
  warning: '#F39C12'
};

interface TelegramChannel {
  id: string;
  username: string;
  name: string;
  imageUrl?: string;
  category: string;
  isActive: boolean;
  scrapingEnabled: boolean;
  totalJobsScraped: number;
  lastScraped: any | null;
  createdAt: any;
  updatedAt: any;
}

export default function TelegramChannelsScreen({ colors = defaultColors }: { colors?: any }) {
  const [channels, setChannels] = useState<TelegramChannel[]>([]);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [showAddModal, setShowAddModal] = useState(false);
  const [scrapingStatus, setScrapingStatus] = useState<'idle' | 'scraping' | 'completed' | 'error'>('idle');
  const [scrapingProgress, setScrapingProgress] = useState(0);
  const [scrapingMessage, setScrapingMessage] = useState('');
  const [currentSessionId, setCurrentSessionId] = useState<string | null>(null);
  const [scrapingDetails, setScrapingDetails] = useState<{
    totalChannels: number;
    processedChannels: number;
    currentChannel: string | null;
    totalJobsExtracted: number;
    errors: string[];
  }>({
    totalChannels: 0,
    processedChannels: 0,
    currentChannel: null,
    totalJobsExtracted: 0,
    errors: []
  });

  const manualScrapeChannels = httpsCallable(functions, 'runTelegramScrapingNowV2');

  // Listen for real-time progress updates
  useEffect(() => {
    if (!currentSessionId) return;

    console.log('ðŸ“¡ Setting up progress listener for session:', currentSessionId);

    const progressDocRef = doc(db, 'scrapingProgress', currentSessionId);
    const unsubscribe = onSnapshot(progressDocRef, (snapshot) => {
      if (snapshot.exists()) {
        const data = snapshot.data();
        console.log('ðŸ“Š Progress update:', data);

        setScrapingDetails({
          totalChannels: data.totalChannels || 0,
          processedChannels: data.processedChannels || 0,
          currentChannel: data.currentChannel,
          totalJobsExtracted: data.totalJobsExtracted || 0,
          errors: data.errors || []
        });

        // Calculate progress percentage
        const progress = data.totalChannels > 0 
          ? Math.round((data.processedChannels / data.totalChannels) * 100)
          : 0;
        setScrapingProgress(progress);

        // Update message
        if (data.status === 'running') {
          setScrapingMessage(
            data.currentChannel 
              ? `Processing ${data.currentChannel}... (${data.processedChannels}/${data.totalChannels})`
              : `Scraping in progress... (${data.processedChannels}/${data.totalChannels})`
          );
        } else if (data.status === 'completed') {
          setScrapingMessage(`âœ… Completed! Extracted ${data.totalJobsExtracted} jobs from ${data.totalChannels} channels`);
          setScrapingStatus('completed');

          // Show completion alert with results
          const errorDetails = data.errors?.length 
            ? `\n\nâš ï¸ Encountered ${data.errors.length} error(s):\n${data.errors.slice(0, 3).join('\n')}${data.errors.length > 3 ? `\n... and ${data.errors.length - 3} more` : ''}`
            : '';

          Alert.alert(
            data.errors?.length ? 'Scraping Completed with Warnings âš ï¸' : 'Scraping Complete! ðŸŽ‰',
            `Processed: ${data.totalChannels} channels\nJobs found: ${data.totalJobsExtracted}\nMessages processed: ${data.totalMessagesProcessed || 0}${errorDetails}`,
            [
              { text: 'View Console', onPress: () => console.log('Full scraping result:', data) },
              { text: 'OK', style: 'default' }
            ]
          );

          // Refresh channels to show updated stats
          loadChannels();

          // Auto-hide progress after 5 seconds
          setTimeout(() => {
            setScrapingStatus('idle');
            setScrapingProgress(0);
            setScrapingMessage('');
            setCurrentSessionId(null);
          }, 5000);
        } else if (data.status === 'failed') {
          setScrapingMessage(`âŒ Scraping failed: ${data.error || 'Unknown error'}`);
          setScrapingStatus('error');

          // Show error alert
          Alert.alert(
            'Scraping Failed',
            `Scraping failed: ${data.error || 'Unknown error'}\n\nCheck console for details.`,
            [{ text: 'OK' }]
          );

          setTimeout(() => {
            setScrapingStatus('idle');
            setScrapingProgress(0);
            setScrapingMessage('');
            setCurrentSessionId(null);
          }, 3000);
        }
      }
    }, (error) => {
      console.error('âŒ Progress listener error:', error);
    });

    return () => unsubscribe();
  }, [currentSessionId]);

  const loadChannels = async () => {
    try {
      const channelsQuery = query(
        collection(db, 'telegramChannels'),
        orderBy('createdAt', 'desc')
      );

      const channelsSnapshot = await getDocs(channelsQuery);
      const channelsList: TelegramChannel[] = channelsSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      } as TelegramChannel));

      setChannels(channelsList);
    } catch (error) {
      console.error('Error loading channels:', error);
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  };

  const handleRefresh = async () => {
    setRefreshing(true);
    await loadChannels();
  };

  const handleManualScrape = async () => {
    setScrapingStatus('scraping');
    setScrapingProgress(0);
    setScrapingMessage('Initializing scraper...');

    try {
      console.log('ðŸš€ Starting manual scrape...');
      const result = await manualScrapeChannels();
      console.log('ðŸ“Š Scraping result:', result);

      const data = result.data as any;

      // Set the session ID to start listening for progress
      if (data?.sessionId) {
        console.log('ï¿½ Starting progress tracking for session:', data.sessionId);
        setCurrentSessionId(data.sessionId);
      }

      if (data?.success) {
        // Function returned immediately - scraping is running in background
        console.log('âœ… Scraping started in background:', data);

        // Log any errors that occurred during scraping (will be updated via progress listener)
        if (data.errors && data.errors.length > 0) {
          console.error('âš ï¸ Scraping completed with errors:', data.errors);
          data.errors.forEach((err: string, index: number) => {
            console.error(`  Error ${index + 1}:`, err);
          });
        }

        setScrapingMessage(`Scraping ${data.channelsToProcess} channels in progress...`);

        // Note: Final results will be shown by the progress listener when status becomes 'completed'

        // Refresh channels to show updated stats later
        setTimeout(() => loadChannels(), 5000);
      } else {
        setScrapingMessage('âŒ Scraping failed - check console for details');
        setScrapingStatus('error');

        console.error('âŒ Scraping failed. Full response:', data);

        Alert.alert(
          'Scraping Failed',
          'The scraping process failed. Please check the console for detailed error logs.',
          [{ text: 'OK' }]
        );

        setTimeout(() => {
          setScrapingStatus('idle');
          setScrapingProgress(0);
          setScrapingMessage('');
        }, 3000);
      }

    } catch (error: any) {
      console.error('âŒ Manual scraping failed with exception:', error);
      console.error('Error details:', {
        message: error.message,
        code: error.code,
        details: error.details,
        stack: error.stack
      });

      setScrapingStatus('error');

      const errorMessage = error.message || String(error);
      const errorCode = error.code || 'unknown';
      const errorDetails = error.details || {};

      setScrapingMessage(`âŒ Error: ${errorMessage}`);

      Alert.alert(
        'Scraping Failed',
        `An error occurred during scraping:\n\nError: ${errorMessage}\nCode: ${errorCode}\n\nCheck console for full details.`,
        [
          { 
            text: 'View Console', 
            onPress: () => {
              console.log('=== FULL ERROR DETAILS ===');
              console.error('Error object:', error);
              console.error('Error details:', errorDetails);
            }
          },
          { text: 'OK', style: 'cancel' }
        ]
      );

      setTimeout(() => {
        setScrapingStatus('idle');
        setScrapingProgress(0);
        setScrapingMessage('');
      }, 5000);
    }
  };

  const handleChannelAdded = () => {
    loadChannels();
  };

  const handleChannelsUpdate = () => {
    loadChannels();
  };

  const enableAllChannelsForScraping = async () => {
    try {
      const batch = writeBatch(db);
      let updatedCount = 0;

      for (const channel of channels) {
        if (!channel.isActive || !channel.scrapingEnabled) {
          const channelRef = doc(db, 'telegramChannels', channel.id);
          batch.update(channelRef, {
            isActive: true,
            scrapingEnabled: true,
            updatedAt: serverTimestamp()
          });
          updatedCount++;
        }
      }

      if (updatedCount > 0) {
        await batch.commit();
        Alert.alert(
          'Channels Updated! âœ…',
          `${updatedCount} channels have been enabled for scraping.`,
          [{ text: 'OK' }]
        );
        loadChannels();
      } else {
        Alert.alert(
          'All Set! ðŸ‘',
          'All channels are already enabled for scraping.',
          [{ text: 'OK' }]
        );
      }
    } catch (error) {
      console.error('Error enabling channels:', error);
      Alert.alert(
        'Error',
        'Failed to update channel settings. Please try again.',
        [{ text: 'OK' }]
      );
    }
  };

  useEffect(() => {
    loadChannels();
  }, []);

  return (
    <SafeAreaView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <View style={styles.headerContent}>
          <Image source={NibJobsIcon} style={styles.headerIcon} />
          <View style={styles.headerText}>
            <Text style={styles.headerTitle}>Telegram Channels</Text>
            <Text style={styles.headerSubtitle}>
              Manage job scraping sources
            </Text>
          </View>
        </View>
        <TouchableOpacity 
          style={styles.addButton}
          onPress={() => setShowAddModal(true)}
        >
          <Text style={styles.addButtonText}>+ Add Channel</Text>
        </TouchableOpacity>
      </View>

      {/* Main Content */}
      <ScrollView 
        style={styles.content}
        refreshControl={
          <RefreshControl
            refreshing={refreshing}
            onRefresh={handleRefresh}
            colors={[colors.beeYellow]}
            tintColor={colors.beeYellow}
          />
        }
      >
        {/* Overview Cards */}
        <View style={styles.overviewSection}>
          <Text style={styles.sectionTitle}>Overview</Text>
          <View style={styles.overviewCards}>
            <View style={[styles.overviewCard, styles.overviewCardPrimary]}>
              <View style={styles.overviewIconContainer}>
                <Icon name="layers" size={28} color={colors.beeYellow} />
              </View>
              <Text style={styles.overviewValue}>{channels.length}</Text>
              <Text style={styles.overviewLabel}>Total Channels</Text>
            </View>
            <View style={[styles.overviewCard, styles.overviewCardSuccess]}>
              <View style={styles.overviewIconContainer}>
                <Icon name="check-circle" size={28} color={colors.success} />
              </View>
              <Text style={styles.overviewValue}>
                {channels.filter(c => c.isActive && c.scrapingEnabled).length}
              </Text>
              <Text style={styles.overviewLabel}>Active Channels</Text>
            </View>
            <View style={[styles.overviewCard, styles.overviewCardInfo]}>
              <View style={styles.overviewIconContainer}>
                <Icon name="briefcase" size={28} color={colors.honeyGold} />
              </View>
              <Text style={styles.overviewValue}>
                {channels.reduce((total, ch) => total + ch.totalJobsScraped, 0)}
              </Text>
              <Text style={styles.overviewLabel}>Total Jobs Scraped</Text>
            </View>
          </View>
        </View>

        {/* Quick Actions */}
        <View style={styles.quickActionsSection}>
          <Text style={styles.sectionTitle}>Quick Actions</Text>
          <View style={styles.quickActions}>
            <TouchableOpacity 
              style={[styles.actionButton, styles.actionButtonPrimary]}
              onPress={() => setShowAddModal(true)}
            >
              <View style={styles.actionButtonContent}>
                <View style={styles.actionIconCircle}>
                  <Icon name="add" size={24} color={colors.white} />
                </View>
                <Text style={styles.actionButtonText}>Add Channel</Text>
              </View>
            </TouchableOpacity>

            <TouchableOpacity 
              style={[styles.actionButton, styles.actionButtonSecondary]}
              onPress={handleRefresh}
            >
              <View style={styles.actionButtonContent}>
                <View style={styles.actionIconCircle}>
                  <Icon name="refresh" size={24} color={colors.white} />
                </View>
                <Text style={styles.actionButtonText}>Refresh List</Text>
              </View>
            </TouchableOpacity>

            <TouchableOpacity 
              style={[
                styles.actionButton,
                styles.actionButtonWarning,
                scrapingStatus === 'scraping' && styles.actionButtonDisabled
              ]}
              onPress={scrapingStatus === 'idle' ? handleManualScrape : undefined}
              disabled={scrapingStatus === 'scraping'}
            >
              <View style={styles.actionButtonContent}>
                <View style={styles.actionIconCircle}>
                  {scrapingStatus === 'scraping' ? (
                    <ActivityIndicator size="small" color={colors.white} />
                  ) : (
                    <Icon name="play-circle" size={24} color={colors.white} />
                  )}
                </View>
                <Text style={styles.actionButtonText}>
                  {scrapingStatus === 'scraping' ? 'Scraping...' : 'Start Scrape'}
                </Text>
              </View>
            </TouchableOpacity>

            <TouchableOpacity 
              style={[styles.actionButton, styles.actionButtonSuccess]}
              onPress={enableAllChannelsForScraping}
            >
              <View style={styles.actionButtonContent}>
                <View style={styles.actionIconCircle}>
                  <Icon name="check-circle" size={24} color={colors.white} />
                </View>
                <Text style={styles.actionButtonText}>Enable All</Text>
              </View>
            </TouchableOpacity>
          </View>
        </View>

        {/* Scraping Progress Status */}
        {scrapingStatus !== 'idle' && (
          <View style={styles.progressSection}>
            <Text style={styles.progressTitle}>ðŸ”„ Scraping Status</Text>

            {/* Progress Bar */}
            <View style={styles.progressContainer}>
              <View style={[styles.progressBar, { width: `${scrapingProgress}%` }]} />
              <Text style={styles.progressPercentage}>{scrapingProgress}%</Text>
            </View>

            {/* Progress Message */}
            <Text style={styles.progressMessage}>{scrapingMessage}</Text>

            {/* Detailed Progress Info */}
            {scrapingStatus === 'scraping' && scrapingDetails.totalChannels > 0 && (
              <View style={styles.progressDetails}>
                <View style={styles.progressDetailRow}>
                  <Text style={styles.progressDetailLabel}>ðŸ“º Total Channels:</Text>
                  <Text style={styles.progressDetailValue}>{scrapingDetails.totalChannels}</Text>
                </View>
                <View style={styles.progressDetailRow}>
                  <Text style={styles.progressDetailLabel}>âœ… Processed:</Text>
                  <Text style={styles.progressDetailValue}>
                    {scrapingDetails.processedChannels} / {scrapingDetails.totalChannels}
                  </Text>
                </View>
                {scrapingDetails.currentChannel && (
                  <View style={styles.progressDetailRow}>
                    <Text style={styles.progressDetailLabel}>ðŸ” Current:</Text>
                    <Text style={[styles.progressDetailValue, styles.currentChannel]}>
                      {scrapingDetails.currentChannel}
                    </Text>
                  </View>
                )}
                <View style={styles.progressDetailRow}>
                  <Text style={styles.progressDetailLabel}>ðŸ’¼ Jobs Found:</Text>
                  <Text style={[styles.progressDetailValue, styles.jobsCount]}>
                    {scrapingDetails.totalJobsExtracted}
                  </Text>
                </View>
                {scrapingDetails.errors && scrapingDetails.errors.length > 0 && (
                  <View style={styles.progressDetailRow}>
                    <Text style={styles.progressDetailLabel}>âš ï¸ Errors:</Text>
                    <Text style={[styles.progressDetailValue, styles.errorCount]}>
                      {scrapingDetails.errors.length}
                    </Text>
                  </View>
                )}
              </View>
            )}

            {/* Error List - Show if there are errors */}
            {scrapingDetails.errors && scrapingDetails.errors.length > 0 && (
              <View style={styles.errorSection}>
                <Text style={styles.errorSectionTitle}>âš ï¸ Errors Encountered:</Text>
                <ScrollView style={styles.errorList} nestedScrollEnabled>
                  {scrapingDetails.errors.slice(0, 5).map((error, index) => (
                    <View key={index} style={styles.errorItem}>
                      <Text style={styles.errorNumber}>{index + 1}.</Text>
                      <Text style={styles.errorText} numberOfLines={2}>{error}</Text>
                    </View>
                  ))}
                  {scrapingDetails.errors.length > 5 && (
                    <Text style={styles.errorMore}>
                      ... and {scrapingDetails.errors.length - 5} more errors (check console)
                    </Text>
                  )}
                </ScrollView>
              </View>
            )}

            {scrapingProgress > 0 && (
              <Text style={styles.progressPercent}>{scrapingProgress}%</Text>
            )}
          </View>
        )}

        {/* Channels List */}
