  success: '#27AE60',
  danger: '#E74C3C'
};

// Login Screen
function LoginScreen({ navigation }) {
  const [email, setEmail] = useState('nathanmersha@gmail.com');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);

  const handleLogin = async () => {
    if (!email || !password) {
      Alert.alert('Error', 'Please enter email and password');
      return;
    }

    setLoading(true);
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // Check if user is admin
      const userDoc = await getDoc(doc(db, 'users', user.uid));
      if (userDoc.exists() && userDoc.data().role === 'superadmin') {
        navigation.replace('AdminPanel');
      } else {
        Alert.alert('Access Denied', 'You do not have admin privileges');
        await signOut(auth);
      }
    } catch (error) {
      Alert.alert('Login Failed', error.message);
    }
    setLoading(false);
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.loginContainer}>
        <Text style={styles.logo}>ğŸ NibJobs Admin</Text>
        <Text style={styles.subtitle}>Administrator Login</Text>

        <View style={styles.inputContainer}>
          <Text style={styles.label}>Email</Text>
          <TextInput
            style={styles.input}
            value={email}
            onChangeText={setEmail}
            placeholder="Enter admin email"
            placeholderTextColor={colors.warmGray}
            keyboardType="email-address"
            autoCapitalize="none"
          />
        </View>

        <View style={styles.inputContainer}>
          <Text style={styles.label}>Password</Text>
          <TextInput
            style={styles.input}
            value={password}
            onChangeText={setPassword}
            placeholder="Enter password"
            placeholderTextColor={colors.warmGray}
            secureTextEntry
          />
        </View>

        <TouchableOpacity 
          style={[styles.loginButton, loading && styles.loginButtonDisabled]} 
          onPress={handleLogin}
          disabled={loading}
        >
          <Text style={styles.loginButtonText}>
            {loading ? 'Signing In...' : 'Sign In'}
          </Text>
        </TouchableOpacity>

        <Text style={styles.note}>
          Super Admin Access Only
        </Text>
      </View>
    </SafeAreaView>
  );
}

// Admin Panel Screen with Sidebar
function AdminPanelScreen({ navigation }) {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [user, setUser] = useState<any>(null);

  // URL query-based routing for maintaining state on refresh
  useEffect(() => {
    if (typeof window !== 'undefined') {
      const urlParams = new URLSearchParams(window.location.search);
      const tabFromURL = urlParams.get('tab');
      if (tabFromURL && ['dashboard', 'categories', 'jobs', 'telegram', 'users', 'analytics'].includes(tabFromURL)) {
        setActiveTab(tabFromURL);
      }
    }
  }, []);

  // Update URL when tab changes
  const handleTabChange = (tabId: string) => {
    setActiveTab(tabId);
    if (typeof window !== 'undefined') {
      const url = new URL(window.location.href);
      url.searchParams.set('tab', tabId);
      window.history.replaceState({}, '', url.toString());
    }
  };

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (authUser) => {
      if (authUser) {
        const userDoc = await getDoc(doc(db, 'users', authUser.uid));
        if (userDoc.exists()) {
          setUser({ ...authUser, ...userDoc.data() });
        }
      }
    });
    return unsubscribe;
  }, []);

  const handleLogout = async () => {
    await signOut(auth);
    navigation.replace('Login');
  };

  const sidebarItems = [
    { id: 'dashboard', title: 'Dashboard', icon: 'ğŸ“Š' },
    { id: 'categories', title: 'Job Categories', icon: 'ğŸ“‹' },
    { id: 'jobs', title: 'Jobs', icon: 'ğŸ’¼' },
    { id: 'telegram', title: 'Telegram Channels', icon: 'ğŸ“±' },
    { id: 'users', title: 'Users', icon: 'ğŸ‘¥' },
    { id: 'analytics', title: 'Analytics', icon: 'ğŸ“ˆ' }
  ];

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.adminContainer}>
        {/* Sidebar */}
        <View style={styles.sidebar}>
          <View style={styles.sidebarHeader}>
            <Text style={styles.sidebarLogo}>ğŸ Admin</Text>
            <Text style={styles.sidebarUser}>
              {user ? `ğŸ‘¨â€ğŸ’¼ ${user.profile?.firstName || 'Admin'}` : 'Loading...'}
            </Text>
          </View>

          {sidebarItems.map((item) => (
            <TouchableOpacity
              key={item.id}
              style={[
                styles.sidebarItem,
                activeTab === item.id && styles.sidebarItemActive
              ]}
              onPress={() => handleTabChange(item.id)}
            >
              <Text style={styles.sidebarIcon}>{item.icon}</Text>
              <Text style={[
                styles.sidebarText,
                activeTab === item.id && styles.sidebarTextActive
              ]}>
                {item.title}
              </Text>
            </TouchableOpacity>
          ))}

          <TouchableOpacity style={styles.logoutButton} onPress={handleLogout}>
            <Text style={styles.logoutIcon}>ğŸšª</Text>
            <Text style={styles.logoutText}>Logout</Text>
          </TouchableOpacity>
        </View>

        {/* Main Content */}
        <View style={styles.mainContent}>
          {activeTab === 'dashboard' && <DashboardContent />}
          {activeTab === 'categories' && <CategoriesContent />}
          {activeTab === 'jobs' && <JobsContent />}
          {activeTab === 'telegram' && <TelegramChannelsScreen />}
          {activeTab === 'users' && <UsersContent />}
          {activeTab === 'analytics' && <AnalyticsContent />}
        </View>
      </View>
    </SafeAreaView>
  );
}

