        {'\n\n'}
        Use the sidebar to manage job categories, jobs, and users.
      </Text>
    </View>
  );
}

// Categories Content
function CategoriesContent() {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    // In a real app, fetch from Firestore
    setCategories([
      { id: 'technology', name: 'Technology', icon: 'ğŸ’»', count: 1 },
      { id: 'design', name: 'Design & Creative', icon: 'ğŸ¨', count: 1 },
      { id: 'marketing', name: 'Marketing & Sales', icon: 'ğŸ“ˆ', count: 1 },
      { id: 'finance', name: 'Finance & Accounting', icon: 'ğŸ’°', count: 0 },
      { id: 'healthcare', name: 'Healthcare', icon: 'ğŸ¥', count: 0 }
    ]);
  }, []);

  return (
    <View style={styles.contentContainer}>
      <Text style={styles.contentTitle}>ğŸ“‹ Job Categories Management</Text>

      <TouchableOpacity style={styles.addButton}>
        <Text style={styles.addButtonText}>+ Add New Category</Text>
      </TouchableOpacity>

      <View style={styles.listContainer}>
        {categories.map((category) => (
          <View key={category.id} style={styles.listItem}>
            <Text style={styles.listIcon}>{category.icon}</Text>
            <View style={styles.listInfo}>
              <Text style={styles.listTitle}>{category.name}</Text>
              <Text style={styles.listSubtitle}>{category.count} jobs</Text>
            </View>
            <View style={styles.listActions}>
