              </TouchableOpacity>
              <TouchableOpacity style={styles.deleteButton}>
                <Text style={styles.actionText}>Delete</Text>
              </TouchableOpacity>
            </View>
          </View>
        ))}
      </View>
    </View>
  );
}

// Jobs Content
interface Job {
  id: string;
  title: string;
  company: string;
  category: string;
  contractType: string;
  location: string;
  salary?: string;
  jobSource: string;
  postedDate?: Date;
  createdAt?: Date;
  isRemote: boolean;
  tags: string[];
}

function JobsContent() {
  const [jobs, setJobs] = useState<Job[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const loadJobs = async () => {
    try {
      setLoading(true);
      console.log('ğŸ“‹ Loading jobs from Firestore...');

      // Query the jobs collection, ordered by creation date, limit to latest 50
      const jobsQuery = query(
        collection(db, 'jobs'),
        orderBy('createdAt', 'desc'),
        limit(50)
      );

      const querySnapshot = await getDocs(jobsQuery);
      const jobsData: Job[] = [];

      querySnapshot.forEach((doc) => {
        const data = doc.data();
        jobsData.push({
          id: doc.id,
          title: data.title || 'No title',
          company: data.company || 'Unknown company',
          category: data.category || 'Other',
          contractType: data.contractType || 'Full-time',
          location: data.location || 'Not specified',
          salary: data.salary,
          jobSource: data.jobSource || 'Unknown',
          postedDate: data.postedDate ? data.postedDate.toDate() : null,
          createdAt: data.createdAt ? data.createdAt.toDate() : null,
          isRemote: data.isRemote || false,
          tags: data.tags || []
        });
      });

      console.log(`âœ… Loaded ${jobsData.length} jobs from Firestore`);
      setJobs(jobsData);
      setError(null);

    } catch (err) {
      console.error('âŒ Error loading jobs:', err);
      setError(err instanceof Error ? err.message : 'Failed to load jobs');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadJobs();
  }, []);

  return (
    <View style={styles.contentContainer}>
      <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', marginBottom: 20 }}>
        <Text style={styles.contentTitle}>ğŸ’¼ Jobs ({jobs.length})</Text>
        <TouchableOpacity 
          style={[styles.addButton, { backgroundColor: colors.beeYellow }]}
          onPress={loadJobs}
        >
          <Text style={[styles.addButtonText, { color: colors.deepNavy }]}>
            {loading ? 'ğŸ”„ Loading...' : 'ğŸ”„ Refresh'}
          </Text>
        </TouchableOpacity>
      </View>

      {error && (
        <View style={{ padding: 15, backgroundColor: colors.danger, borderRadius: 8, marginBottom: 15 }}>
          <Text style={{ color: colors.white, fontWeight: 'bold' }}>âŒ Error loading jobs</Text>
          <Text style={{ color: colors.white }}>{error}</Text>
        </View>
      )}

      {loading && jobs.length === 0 ? (
        <View style={{ padding: 20, alignItems: 'center' }}>
          <Text style={{ fontSize: 16, color: colors.warmGray }}>ğŸ“‹ Loading jobs from database...</Text>
        </View>
      ) : jobs.length === 0 ? (
        <View style={{ padding: 20, alignItems: 'center' }}>
          <Text style={{ fontSize: 16, color: colors.warmGray }}>ğŸ” No jobs found</Text>
          <Text style={{ fontSize: 14, color: colors.warmGray, textAlign: 'center', marginTop: 5 }}>
            Run the Telegram scraping to populate jobs from channels
          </Text>
        </View>
      ) : (
        <ScrollView style={styles.listContainer}>
          {jobs.map((job) => (
            <View key={job.id} style={[styles.listItem, { marginBottom: 10 }]}>
              <View style={{ flexDirection: 'row', alignItems: 'flex-start' }}>
                <Text style={styles.listIcon}>ğŸ’¼</Text>
                <View style={[styles.listInfo, { flex: 1 }]}>
                  <Text style={styles.listTitle}>{job.title}</Text>
                  <Text style={styles.listSubtitle}>
                    ğŸ¢ {job.company} â€¢ ğŸ“‚ {job.category}
                  </Text>
                  {job.location && (
                    <Text style={[styles.listSubtitle, { fontSize: 12 }]}>
                      ğŸ“ {job.location}
                    </Text>
                  )}
                  {job.salary && (
                    <Text style={[styles.listSubtitle, { fontSize: 12, color: colors.success }]}>
                      ğŸ’° {job.salary}
                    </Text>
                  )}
                  <View style={{ flexDirection: 'row', alignItems: 'center', marginTop: 5 }}>
                    <Text style={[styles.statusBadge, { 
                      backgroundColor: colors.beeYellow, 
                      color: colors.deepNavy,
                      fontSize: 11,
                      paddingHorizontal: 8,
                      paddingVertical: 2,
                      borderRadius: 12,
                      marginRight: 5
                    }]}>
                      {job.contractType}
                    </Text>
                    {job.isRemote && (
                      <Text style={[styles.statusBadge, { 
                        backgroundColor: colors.success, 
                        color: colors.white,
                        fontSize: 11,
                        paddingHorizontal: 8,
                        paddingVertical: 2,
                        borderRadius: 12,
                        marginRight: 5
                      }]}>
                        ğŸŒ Remote
                      </Text>
                    )}
                  </View>
                  <Text style={[styles.listSubtitle, { fontSize: 11, marginTop: 5 }]}>
                    ğŸ“¡ {job.jobSource} â€¢ â° {job.createdAt ? job.createdAt.toLocaleDateString() : 'Unknown date'}
                  </Text>
                </View>
              </View>
            </View>
          ))}
        </ScrollView>
      )}
    </View>
  );
}

// Users Content
