            {/* Detailed Progress Info */}
            {scrapingStatus === 'scraping' && scrapingDetails.totalChannels > 0 && (
              <View style={styles.progressDetails}>
                <View style={styles.progressDetailRow}>
                  <Text style={styles.progressDetailLabel}>üì∫ Total Channels:</Text>
                  <Text style={styles.progressDetailValue}>{scrapingDetails.totalChannels}</Text>
                </View>
                <View style={styles.progressDetailRow}>
                  <Text style={styles.progressDetailLabel}>‚úÖ Processed:</Text>
                  <Text style={styles.progressDetailValue}>
                    {scrapingDetails.processedChannels} / {scrapingDetails.totalChannels}
                  </Text>
                </View>
                {scrapingDetails.currentChannel && (
                  <View style={styles.progressDetailRow}>
                    <Text style={styles.progressDetailLabel}>üîç Current:</Text>
                    <Text style={[styles.progressDetailValue, styles.currentChannel]}>
                      {scrapingDetails.currentChannel}
                    </Text>
                  </View>
                )}
                <View style={styles.progressDetailRow}>
                  <Text style={styles.progressDetailLabel}>üíº Jobs Found:</Text>
                  <Text style={[styles.progressDetailValue, styles.jobsCount]}>
                    {scrapingDetails.totalJobsExtracted}
                  </Text>
                </View>
                {scrapingDetails.errors && scrapingDetails.errors.length > 0 && (
                  <View style={styles.progressDetailRow}>
                    <Text style={styles.progressDetailLabel}>‚ö†Ô∏è Errors:</Text>
                    <Text style={[styles.progressDetailValue, styles.errorCount]}>
                      {scrapingDetails.errors.length}
                    </Text>
                  </View>
                )}
              </View>
            )}

            {/* Error List - Show if there are errors */}
            {scrapingDetails.errors && scrapingDetails.errors.length > 0 && (
              <View style={styles.errorSection}>
                <Text style={styles.errorSectionTitle}>‚ö†Ô∏è Errors Encountered:</Text>
                <ScrollView style={styles.errorList} nestedScrollEnabled>
                  {scrapingDetails.errors.slice(0, 5).map((error, index) => (
                    <View key={index} style={styles.errorItem}>
                      <Text style={styles.errorNumber}>{index + 1}.</Text>
                      <Text style={styles.errorText} numberOfLines={2}>{error}</Text>
                    </View>
                  ))}
                  {scrapingDetails.errors.length > 5 && (
                    <Text style={styles.errorMore}>
                      ... and {scrapingDetails.errors.length - 5} more errors (check console)
                    </Text>
                  )}
                </ScrollView>
              </View>
            )}

            {scrapingProgress > 0 && (
              <Text style={styles.progressPercent}>{scrapingProgress}%</Text>
            )}
          </View>
        )}

        {/* Channels List */}
        <View style={styles.channelsSection}>
          <TelegramChannelsList
            channels={channels}
            onChannelsUpdate={handleChannelsUpdate}
            loading={loading}
          />
        </View>
      </ScrollView>

      {/* Add Channel Modal */}
      <TelegramChannelFormModal
        visible={showAddModal}
        onClose={() => setShowAddModal(false)}
        onChannelAdded={handleChannelAdded}
      />
    </SafeAreaView>
  );
}

